INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16172,3388,'A','<p>mais eficiente em pH b&aacute;sico.</p>\r\n','<p>CORRETA<br />\r\nO &aacute;cido carbox&iacute;lico &eacute; pouco eficiente na remo&ccedil;&atilde;o de gorduras, portanto, o equil&iacute;brio dever&aacute; ser deslocado no sentido de manter esse &aacute;cido em sua forma ionizada, que encontra-se nos reagentes. Assim, &eacute; preciso deslocar o equil&iacute;brio no sentido dos reagentes, o que pode ocorrer pelo aumento da concentra&ccedil;&atilde;o de &iacute;ons hidroxila. Isso implica que os sab&otilde;es atuam de maneira mais eficiente em pH b&aacute;sico.&nbsp;</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16173,3388,'B','<p>mais eficiente em pH &aacute;cido.</p>\r\n','<p>INCORRETA<br />\r\nN&atilde;o compreende o sentido do deslocamento do equil&iacute;brio apresentado, visto que em pH &aacute;cido ocorre aumento da concentra&ccedil;&atilde;o do &aacute;cido carbox&iacute;lico, que &eacute; menos eficiente na remo&ccedil;&atilde;o de gorduras.</p>\r\n',0);
