INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (4074,828,'C','<p>Manter o menor n&uacute;mero poss&iacute;vel de aparelhos el&eacute;tricos em funcionamento nas moradias.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que seleciona essa alternativa acredita que a economia de energia aumentar&aacute; a efici&ecirc;ncia de um processo de convers&atilde;o, ou seja, n&atilde;o entende que a efici&ecirc;ncia &eacute; um par&acirc;metro que n&atilde;o depende do consumo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (4075,828,'D','<p>Utilizar cabos com menor di&acirc;metro nas linhas de transmiss&atilde;o a fim de economizar o material condutor.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que seleciona essa alternativa n&atilde;o sabe que diminuir o di&acirc;metro ir&aacute; aumentar perdas por Efeito Joule e, ainda, acredita que a economia financeira no material compensar&aacute; a perda de efici&ecirc;ncia do processo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (4076,828,'E','<p>Utilizar materiais com melhores propriedades condutoras nas linhas de transmiss&atilde;o e l&acirc;mpadas fluorescentes nas moradias.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Aumentar a efici&ecirc;ncia de um processo consiste em diminuir fontes de perda de energia. Utilizar materiais com melhores propriedades condutoras e l&acirc;mpadas fluorescentes ir&aacute; diminuir a perda de energia por Efeito Joule (aquecimento dos fios) e, portanto, aumentar&aacute; a efici&ecirc;ncia geral do processo.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16177,3389,'A','<p>Au, Pt, Ag, Zn, Ni e Pb.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que selecionar essa alternativa n&atilde;o compreende a escala de for&ccedil;as redutoras e acredita que todo metal que n&atilde;o seja o cobre sedimentar&aacute; abaixo do &acirc;nodo de cobre.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16178,3389,'B','<p>Au, Pt e Ag.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>A for&ccedil;a redutora consiste na capacidade que um metal tem para provocar a redu&ccedil;&atilde;o de um outro metal. Dessa forma, o metal redutor dever&aacute; perder el&eacute;trons (oxidar) com mais facilidade que o metal a ser reduzido e, assim, fornecer os el&eacute;trons necess&aacute;rios para que ocorra a redu&ccedil;&atilde;o da esp&eacute;cie desejada. Quanto menor for a for&ccedil;a redutora, maior ser&aacute; a tend&ecirc;ncia de uma esp&eacute;cie ser reduzida, e n&atilde;o oxidada. Observando a s&eacute;rie apresentada, o ouro, a platina e a prata apresentam a menor for&ccedil;a redutora e, portanto, sedimentam-se abaixo do &acirc;nodo de cobre, pois n&atilde;o ser&atilde;o oxidados.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16179,3389,'C','<p>Zn, Ni e Pb.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que selecionar essa alternativa n&atilde;o compreende o sentido da for&ccedil;a redutora na representa&ccedil;&atilde;o esquem&aacute;tica.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16180,3389,'D','<p>Au e Zn.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que selecionar essa alternativa n&atilde;o compreende o sentido da for&ccedil;a redutora na representa&ccedil;&atilde;o esquem&aacute;tica e acredita que apenas os metais com menor e maior for&ccedil;a redutora ser&atilde;o capazes de sedimentar abaixo do &acirc;nodo de cobre.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16181,3389,'E','<p>Ag e Pb.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que selecionar essa alternativa n&atilde;o compreende o sentido da for&ccedil;a redutora na representa&ccedil;&atilde;o esquem&aacute;tica e acredita que apenas os metais imediatamente com a primeira menor e a primeira maior for&ccedil;a redutora em rela&ccedil;&atilde;o ao cobre ser&atilde;o capazes de sedimentar abaixo do &acirc;nodo de cobre.</p>\r\n',0);
