INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16182,3390,'A','<p>HNO<sub>3</sub> e HNO<sub>2</sub></p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o compreende que o &aacute;cido nitroso (HNO2) &eacute; um &aacute;cido moderado.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16183,3390,'B','<p>H<sub>2</sub>SO<sub>4 </sub>e H<sub>2</sub>SO<sub>3</sub></p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o compreende que o &aacute;cido sulfuroso (H2SO3) &eacute; um &aacute;cido moderado.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16184,3390,'C','<p>H<sub>2</sub>SO<sub>3</sub> e HNO<sub>2</sub></p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o compreende que o &aacute;cido nitroso (HNO2) e o &aacute;cido sulfuroso(H2SO3) s&atilde;o &aacute;cidos moderado.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16185,3390,'D','<p>H<sub>2</sub>SO<sub>4</sub> e HNO<sub>3</sub></p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Como os &aacute;cidos apresentados, s&atilde;o oxi&aacute;cidos, pode-se estabelecer uma rela&ccedil;&atilde;o estimativa da acidez por meio da quantidade de oxig&ecirc;nios e de hidrog&ecirc;nios ioniz&aacute;veis, obtendo-se uma escala de for&ccedil;a &aacute;cida, sendo:</p>\r\n\r\n<p>x = quantidade de oxig&ecirc;nios - n&uacute;mero de hidrog&ecirc;nios ioniz&aacute;veis, em que:</p>\r\n\r\n<p>x = 0 &aacute;cidos fracos</p>\r\n\r\n<p>x = 1 &aacute;cidos semifortes ou moderados</p>\r\n\r\n<p>x = 2 ou 3 &aacute;cidos fortes.</p>\r\n\r\n<p>Assim, tem-se:</p>\r\n\r\n<p>H2SO4: 4 - 2 = 2 &aacute;cido forte</p>\r\n\r\n<p>HNO3: 3 - 1 = 2 &aacute;cido forte</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (16186,3390,'E','<p>H<sub>2</sub>CO<sub>3</sub> e H<sub>2</sub>SO<sub>3</sub></p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o compreende que o &aacute;cido carb&ocirc;nico (H2CO3) &eacute; um &aacute;cido fraco, nem que o &aacute;cido sulfuroso (H2SO3) &eacute; um &aacute;cido moderado.</p>\r\n',0);
