INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1927,391,'C','<p>&Agrave; &eacute;poca das conquistas, a Am&eacute;rica Latina, a &Aacute;frica e a &Aacute;sia tinham sistemas pol&iacute;ticos e administrativos muito mais sofisticados que aqueles que lhes foram impostos pelo colonizador.</p>\r\n','',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1928,391,'D','<p>Comparadas ao M&eacute;xico e ao Peru, as institui&ccedil;&otilde;es brasileiras, por terem sido eliminadas &agrave; &eacute;poca da conquista, sofreram mais influ&ecirc;ncia dos modelos institucionais europeus.</p>\r\n','',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1929,391,'E','<p>O modelo hist&oacute;rico da forma&ccedil;&atilde;o do Estado asi&aacute;tico equipara-se ao brasileiro, pois em ambos se manteve o esp&iacute;rito das formas de organiza&ccedil;&atilde;o anteriores &agrave; conquista.</p>\r\n','',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1930,392,'A','<p>dos rumores acerca da revolta escrava do Haiti, que circulavam entre a popula&ccedil;&atilde;o escrava e entre os mesti&ccedil;os pobres, alimentando seu desejo por mudan&ccedil;as.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>A revolta do Haiti cuja alternativa faz refer&ecirc;ncia &eacute; o movimento que culminou na Independ&ecirc;ncia do territ&oacute;rio haitiano, colonizado at&eacute; ent&atilde;o pela Fran&ccedil;a. O car&aacute;ter revolucion&aacute;rio da revolta, que representou a tomada de poder por parte de negros escravizados for&ccedil;adamente deslocados da &Aacute;frica, era emblem&aacute;tico para muitos movimentos nascentes no Brasil colonial, que tinham em comum o desejo por uma mudan&ccedil;a em rela&ccedil;&atilde;o &agrave; depend&ecirc;ncia pol&iacute;tica em rela&ccedil;&atilde;o a Portugal, mas principalmente em rela&ccedil;&atilde;o ao fim do regime de escravid&atilde;o e segrega&ccedil;&atilde;o racial que castigava a vida da popula&ccedil;&atilde;o escrava e mesti&ccedil;os pobres na col&ocirc;nia.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1931,392,'B','<p>da rejei&ccedil;&atilde;o aos portugueses, brancos, que significava a rejei&ccedil;&atilde;o &agrave; opress&atilde;o da Metr&oacute;pole, como ocorreu na Noite das Garrafadas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A rejei&ccedil;&atilde;o aos portugueses, brancos, que protagonizava muitos movimentos que se inspiravam na revolta escrava do Haiti tinha um car&aacute;ter muito diferente daquela que culminou na Noite das Garrafadas. Enquanto a primeira implicava uma avers&atilde;o total ao estatuto da escraviza&ccedil;&atilde;o de negros por brancos estabelecido nas col&ocirc;nias americanas, a segunda foi um epis&oacute;dio em que brasileiros, descontentes com Dom Pedro I e seus aliados, portugueses, se manifestaram em uma visita do Imperador a Ouro Preto. O ocorrido foi resultado da morte de L&iacute;bero Badar&oacute;, cr&iacute;tico ferrenho do Imperador, e que teria sido assassinado por uma ordem de Dom Pedro I.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1932,392,'C','<p>do apoio que escravos e negros forros deram &agrave; monarquia, com a perspectiva de receber sua prote&ccedil;&atilde;o contra as injusti&ccedil;as do sistema escravista.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Os versos cantados pelas classes populares em Recife s&atilde;o categ&oacute;ricos ao revelar a rejei&ccedil;&atilde;o em rela&ccedil;&atilde;o aos brancos da sociedade brasileira e, ainda mais al&eacute;m, revelam tamb&eacute;m o desejo de que apenas &ldquo;pardos e pretos&rdquo; habitem o pa&iacute;s. Assim, n&atilde;o est&atilde;o relacionados a um suposto apoio de escravos e negros forros &agrave; Monarquia &ndash; que era a via institucional mantenedora do sistema escravista no Brasil.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1933,392,'D','<p>do rep&uacute;dio que os escravos trabalhadores dos portos demonstravam contra os marinheiros, porque estes representavam a elite branca opressora.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A categoria de &ldquo;marinheiros&rdquo;, presente nos versos apresentados, se refere a portugueses que exerciam essa fun&ccedil;&atilde;o. Apesar de serem brancos e possu&iacute;rem diversos privil&eacute;gios na sociedade colonial, eles n&atilde;o compunham a elite branca.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1934,392,'E','<p>da expuls&atilde;o de v&aacute;rios l&iacute;deres negros independentistas, que defendiam a implanta&ccedil;&atilde;o de uma rep&uacute;blica negra, a exemplo do Haiti.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O sucesso da revolta escrava do Haiti serviu de exemplo para muitos movimentos com motiva&ccedil;&otilde;es raciais no Brasil colonial. &nbsp;Diferentemente do que a alternativa aponta, no Haiti as lideran&ccedil;as negras instauraram uma monarquia.</p>\r\n',0);
