INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1960,398,'A','<p>possu&iacute;rem o mesmo n&iacute;vel de influ&ecirc;ncia no cen&aacute;rio mundial.</p>\r\n','<p>INCORRETA<br />\r\nO n&iacute;vel de influ&ecirc;ncia das cidades globais no cen&aacute;rio mundial &eacute; vari&aacute;vel, diferindo de uma para outra. S&atilde;o tomadas como um grupo para fins de classifica&ccedil;&atilde;o, mas, apesar disso, n&atilde;o h&aacute; homogeneidade na sua import&acirc;ncia, bem como n&atilde;o o h&aacute; na sua influ&ecirc;ncia, j&aacute; que as rela&ccedil;&otilde;es diferem entre elas e as &aacute;reas com as quais se vinculam.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1961,398,'B','<p>fortalecerem os la&ccedil;os de cidadania e solidariedade entre os membros das diversas comunidades.</p>\r\n','<p>INCORRETA<br />\r\nN&atilde;o existem fortes la&ccedil;os de cidadania e solidariedade entre os membros das diversas comunidades das cidades globais. Ao contr&aacute;rio. Pela sua dimens&atilde;o f&iacute;sica, econ&ocirc;mica, cultural, entre outros aspectos, elas s&atilde;o bastante impessoais, apresentando pessoas das diversas partes do mundo que ali vivem ou ali visitam com fins diversos.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1962,398,'C','<p>constitu&iacute;rem um passo importante para a diminui&ccedil;&atilde;o das desigualdades sociais causadas pela polariza&ccedil;&atilde;o social e pela segrega&ccedil;&atilde;o urbana.</p>\r\n','<p>INCORRETA<br />\r\nAs cidades globais n&atilde;o constituem passo importante para a diminui&ccedil;&atilde;o das desigualdades sociais causadas pela polariza&ccedil;&atilde;o social e pela segrega&ccedil;&atilde;o urbana. Ao contr&aacute;rio. Elas contribuem para sua amplia&ccedil;&atilde;o, uma vez que se revelam &aacute;reas de concentra&ccedil;&atilde;o de poder e riquezas e est&atilde;o no topo da hierarquia urbana.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1963,398,'D','<p>terem sido diretamente impactadas pelo processo de internacionaliza&ccedil;&atilde;o da economia, desencadeado a partir do final dos anos 1970.</p>\r\n','<p>CORRETA<br />\r\nTamb&eacute;m conhecidas como metr&oacute;poles mundiais, cidades globais s&atilde;o aquelas que possuem influ&ecirc;ncia em n&iacute;vel mundial. Elas influenciam nos centros urbanos do pr&oacute;prio pa&iacute;s e tamb&eacute;m em regi&otilde;es de outros pa&iacute;ses do mesmo e de outros continentes. As cidades globais possuem grande import&acirc;ncia para a economia mundial. Recebem estudantes, trabalhadores qualificados e pesquisadores do mundo todo. Geram, al&eacute;m de riqueza, quantidade significativa de conhecimentos cient&iacute;ficos de qualidade. Em fun&ccedil;&atilde;o da vida cultural din&acirc;mica, s&atilde;o tamb&eacute;m importantes centros culturais, recendo grandes quantidades de turistas anualmente. S&atilde;o importantes no processo de globaliza&ccedil;&atilde;o econ&ocirc;mica e cultural, que vem ocorrendo no mundo nas &uacute;ltimas d&eacute;cadas, sendo, inclusive, viabilizadas por ele. Portanto, elas se caracterizam por terem sido diretamente impactadas pelo processo de internacionaliza&ccedil;&atilde;o da economia, desencadeado a partir do final dos anos 1970, o que torna essa alternativa o gabarito.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1964,398,'E','<p>terem sua origem diretamente relacionadas ao processo de coloniza&ccedil;&atilde;o ocidental do s&eacute;culo XIX.</p>\r\n','<p>INCORRETA<br />\r\nAs cidades globais n&atilde;o t&ecirc;m sua origem diretamente relacionada ao processo de coloniza&ccedil;&atilde;o ocidental do s&eacute;culo XIX. &Eacute; um termo que foi introduzido posteriormente a esse contexto hist&oacute;rico. A express&atilde;o cidade global, em oposi&ccedil;&atilde;o &agrave; megacidade, foi introduzida por Saskia Sassen, em refer&ecirc;ncia a Londres, Nova Iorque e T&oacute;quio, em sua obra de 1991, A Cidade Global. O termo cidade mundial j&aacute; tinha sido usado por Patrick Geddes, em 1915, para descrever as cidades que controlam uma quantidade desproporcional de neg&oacute;cios globais.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2005,407,'A','<p>a ampla difus&atilde;o das tecnologias de informa&ccedil;&atilde;o nos centros urbanos e no meio rural suscita o contato entre diferentes culturas e, ao mesmo tempo, traz a necessidade de reformular as concep&ccedil;&otilde;es tradicionais de educa&ccedil;&atilde;o.</p>\r\n','',1);
