INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2135,433,'A','<p>a diminui&ccedil;&atilde;o do com&eacute;rcio entre pa&iacute;ses e regi&otilde;es, que se tornaram autossuficientes na produ&ccedil;&atilde;o de bens e servi&ccedil;os.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A diminui&ccedil;&atilde;o do com&eacute;rcio entre pa&iacute;ses e regi&otilde;es n&atilde;o faz parte da nossa realidade. Ao contr&aacute;rio, o com&eacute;rcio mundial s&oacute; se intensifica, uma vez que os pa&iacute;ses s&atilde;o cada vez mais interdependentes, e n&atilde;o autossuficientes na produ&ccedil;&atilde;o de bens e servi&ccedil;os, conforme defende essa alternativa, o que a torna incorreta.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2136,433,'B','<p>a ocorr&ecirc;ncia de desastres ambientais de grandes propor&ccedil;&otilde;es, como no caso de derramamento de &oacute;leo por navios petroleiros.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>A polui&ccedil;&atilde;o por derramamento de petr&oacute;leo &eacute; um tipo grave de polui&ccedil;&atilde;o marinha causada pelo vazamento de petr&oacute;leo de navios ou plataformas. &Eacute; considerado um dos mais graves e problem&aacute;ticos acidentes ambientais em &aacute;guas marinhas. A solu&ccedil;&atilde;o para tal problema implicaria em ado&ccedil;&atilde;o de rigorosas medidas de preven&ccedil;&atilde;o de acidentes em plataformas de petr&oacute;leo, fiscaliza&ccedil;&atilde;o governamental eficiente e constante com rela&ccedil;&atilde;o &agrave; seguran&ccedil;a dos navios petroleiros e plataformas de petr&oacute;leo, manuten&ccedil;&atilde;o constante nas plataformas de petr&oacute;leo, assim como nos petroleiros, aplica&ccedil;&atilde;o de multas pesadas para empresas que descumprem normas de seguran&ccedil;a no transporte e explora&ccedil;&atilde;o de petr&oacute;leo e lavagem de tanques reservat&oacute;rios de petr&oacute;leo em locais adequados, para que a &aacute;gua contaminada n&atilde;o seja despejada no mar. Todo o exposto n&atilde;o condiz com a superexplora&ccedil;&atilde;o dos recursos naturais que &eacute; marca do mundo capitalista, sistema que visa o lucro m&aacute;ximo, sem maiores preocupa&ccedil;&otilde;es com a sustentabilidade, o que faz dessa alternativa o gabarito da quest&atilde;o.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2137,433,'C','<p>a melhora generalizada das condi&ccedil;&otilde;es de vida da popula&ccedil;&atilde;o mundial, a partir da elimina&ccedil;&atilde;o das desigualdades econ&ocirc;micas na atualidade.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Apesar da melhora das condi&ccedil;&otilde;es de vida da popula&ccedil;&atilde;o mundial nos &uacute;ltimos anos, ela n&atilde;o foi homog&ecirc;nea e n&atilde;o &eacute; capaz de eliminar as desigualdades econ&ocirc;micas. Ao contr&aacute;rio, as dist&acirc;ncias socioecon&ocirc;micas s&atilde;o cada vez mais ampliadas no mundo globalizado no qual vivemos, onde impera a superexplora&ccedil;&atilde;o do homem sobre os homens e do homem sobre a natureza, desenhando um mundo injusto e desigual.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2138,433,'D','<p>o desmatamento, que eliminou grandes extens&otilde;es de diversos biomas improdutivos, cujas &aacute;reas passaram a ser ocupadas por centros industriais modernos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O desmatamento alcan&ccedil;a extensas &aacute;reas do mundo. N&atilde;o se pode dizer que um bioma &eacute; improdutivo, pois se incorreria numa l&oacute;gica capitalista de superexplora&ccedil;&atilde;o da natureza, que desconsidera qualquer possibilidade de sustentabilidade.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2139,433,'E','<p>o aumento demogr&aacute;fico mundial, sobretudo nos pa&iacute;ses mais desenvolvidos, que apresentam altas taxas de crescimento vegetativo.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aumento demogr&aacute;fico mundial n&atilde;o pode ser responsabilizado pela crescente intensifica&ccedil;&atilde;o da explora&ccedil;&atilde;o de recursos naturais. Essa seria uma vis&atilde;o neomalthusiana. Essa alternativa est&aacute; ainda incorreta por vincular o maior crescimento aos pa&iacute;ses mais desenvolvidos. Ao contr&aacute;rio do que diz essa alternativa, esses pa&iacute;ses apresentam cada vez mais baixas taxas de crescimento vegetativo.</p>\r\n',0);
