INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2155,437,'A','<p>situar-se em uma zona geopol&iacute;tica mais est&aacute;vel que o Oriente M&eacute;dio.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Com a diminui&ccedil;&atilde;o das calotas polares, novas &aacute;reas energ&eacute;ticas e minerais podem ser exploradas. Desta forma, poder&aacute; existir uma diminui&ccedil;&atilde;o da import&acirc;ncia geopol&iacute;tica no Oriente M&eacute;dio, j&aacute; que n&atilde;o haveria a necessidade de estabelecer alian&ccedil;as ou pactos com pa&iacute;ses ideologicamente e culturalmente diferentes dos pa&iacute;ses ditos ocidentais, que necessitam de grande quantidade desses recursos. Explorar o Polo Norte se revela imensamente mais confort&aacute;vel, por ser uma zona geopol&iacute;tica mais est&aacute;vel que o Oriente M&eacute;dio, conforme indica essa alternativa, gabarito da quest&atilde;o.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2156,437,'B','<p>possibilitar o povoamento de uma regi&atilde;o pouco habitada, al&eacute;m de promover seu desenvolvimento econ&ocirc;mico.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A explora&ccedil;&atilde;o de petr&oacute;leo e min&eacute;rios n&atilde;o visa possibilitar o povoamento de uma regi&atilde;o pouco habitada, al&eacute;m de promover seu desenvolvimento econ&ocirc;mico. Por ser uma &aacute;rea in&oacute;spita, isso n&atilde;o traria benef&iacute;cios financeiros aos exploradores. O interesse maior ali &eacute; obter lucro com esses valiosos recursos naturais.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2157,437,'C','<p>garantir, aos pa&iacute;ses em desenvolvimento, acesso a mat&eacute;rias-primas e energia, necess&aacute;rias ao crescimento econ&ocirc;mico.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A explora&ccedil;&atilde;o de petr&oacute;leo e min&eacute;rios no Polo Norte n&atilde;o visa garantir aos pa&iacute;ses em desenvolvimento acesso a mat&eacute;rias-primas e energia, necess&aacute;rias ao crescimento econ&ocirc;mico. Eles, ainda perif&eacute;ricos, ficam &agrave; margem dessas grandes negocia&ccedil;&otilde;es, inserindo-se muito pouco ou n&atilde;o conseguindo se inserir nesses processos, detidos pelos pa&iacute;ses centrais, que ainda det&eacute;m a hegemonia da explora&ccedil;&atilde;o do mundo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2158,437,'D','<p>contribuir para a redu&ccedil;&atilde;o da polui&ccedil;&atilde;o em &aacute;reas ambientalmente j&aacute; degradadas devido ao grande volume da produ&ccedil;&atilde;o industrial, como ocorreu na Europa.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A explora&ccedil;&atilde;o de petr&oacute;leo e min&eacute;rios no Polo Norte n&atilde;o visa contribuir para a redu&ccedil;&atilde;o da polui&ccedil;&atilde;o em &aacute;reas ambientalmente j&aacute; degradadas devido ao grande volume da produ&ccedil;&atilde;o industrial, como ocorreu na Europa. A quest&atilde;o ambiental est&aacute; ainda, infelizmente, longe de ser grande preocupa&ccedil;&atilde;o dos principais pa&iacute;ses do mundo. Eles se preocupam apenas com a reprodu&ccedil;&atilde;o dos seus lucros, atrav&eacute;s da explora&ccedil;&atilde;o m&aacute;xima dos recursos naturais e dos pr&oacute;prios homens dos pa&iacute;ses perif&eacute;ricos.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2159,437,'E','<p>promover a participa&ccedil;&atilde;o dos combust&iacute;veis f&oacute;sseis na matriz energ&eacute;tica mundial, dominada, majoritariamente, pelas fontes renov&aacute;veis, de maior custo.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A explora&ccedil;&atilde;o de petr&oacute;leo e min&eacute;rios no Polo Norte n&atilde;o visa promover a participa&ccedil;&atilde;o dos combust&iacute;veis f&oacute;sseis na matriz energ&eacute;tica mundial, uma vez que eles j&aacute; dominam esse posto. As fontes renov&aacute;veis ainda est&atilde;o come&ccedil;ando a dividir espa&ccedil;o com eles, apesar da urg&ecirc;ncia ambiental que grita a necessidade de substitui&ccedil;&atilde;o dos combust&iacute;veis f&oacute;sseis.</p>\r\n',0);
