INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2194,444,'E','<p>a primeira entende que as cidades devem ser organismos vivos, que nascem de forma espont&acirc;nea, e a segunda mostra que h&aacute; exemplos hist&oacute;ricos que demonstram o contr&aacute;rio.</p>\r\n','',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2195,445,'A','<p>a cria&ccedil;&atilde;o de parques ecol&oacute;gicos na &aacute;rea do pantanal mato-grossense.</p>\r\n','<p>INCORRETA<br />\r\nO Parque Nacional do Pantanal Matogrossense se situa a sudoeste de Mato Grosso e foi institu&iacute;do com o fim de proteger parte do ecossistema pantaneiro, bem como sua biodiversidade, mantendo o equil&iacute;brio din&acirc;mico e a integridade ecol&oacute;gica dos ecossistemas contidos no Parque. Dessa forma, a conscientiza&ccedil;&atilde;o da sociedade e a organiza&ccedil;&atilde;o de movimentos sociais n&atilde;o precisam exigir a cria&ccedil;&atilde;o de parques ecol&oacute;gicos na &aacute;rea do pantanal mato-grossense. Precisamos &eacute; de maior responsabilidade socioambiental.&nbsp;</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2196,445,'B','<p>a proibi&ccedil;&atilde;o da pesca e da ca&ccedil;a, que tanto amea&ccedil;am a biodiversidade.</p>\r\n','<p>INCORRETA<br />\r\nA pesca, que tanto amea&ccedil;a a biodiversidade, &eacute; regulamentada por lei. Dessa forma, essa pr&aacute;tica n&atilde;o precisa ser proibida, mas fiscalizada, evitando os abusos t&atilde;o comuns nesse cen&aacute;rio. A ca&ccedil;a, por sua vez, n&atilde;o &eacute; regulamentada no Brasil.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2197,445,'C','<p>o aumento das pastagens na &aacute;rea da plan&iacute;cie, para que a cobertura vegetal, composta de gram&iacute;neas, evite a eros&atilde;o do solo.</p>\r\n','<p>INCORRETA<br />\r\nO aumento das pastagens na &aacute;rea da plan&iacute;cie n&atilde;o deve ser exigido, mas extinguido, ou, ao menos, reduzido. A cobertura vegetal, composta de gram&iacute;neas, n&atilde;o tem a mesma capacidade de mitigar os efeitos da eros&atilde;o do solo quando comparada &agrave; vegeta&ccedil;&atilde;o nativa mais densa, que n&atilde;o deveria ser substitu&iacute;da em hip&oacute;tese alguma, a fim de preservar a biodiversidade.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2198,445,'D','<p>o controle do desmatamento e da eros&atilde;o, principalmente nas nascentes dos rios respons&aacute;veis pelo n&iacute;vel das &aacute;guas durante o per&iacute;odo de cheias.</p>\r\n','<p>CORRETA<br />\r\nA oscila&ccedil;&atilde;o do volume est&aacute; ligada diretamente &agrave; origem das &aacute;guas. Quando a varia&ccedil;&atilde;o da quantidade de &aacute;gua de um determinado rio (cheias e vazantes) &eacute; proveniente das &aacute;guas das precipita&ccedil;&otilde;es, como &eacute; o caso do exemplo expresso na quest&atilde;o, o processo &eacute; chamado de regime pluvial. Juntamente com a quantidade de chuvas, um rio tem sua configura&ccedil;&atilde;o determinada por outros agentes, dos quais podemos destacar o relevo (superf&iacute;cie acidentada produz rios de corredeiras, por exemplo), a cobertura vegetal das margens e nascentes, bem como os aspectos do solo. Quando o solo possui pouca impermeabiliza&ccedil;&atilde;o, a amplitude da varia&ccedil;&atilde;o &eacute; grande, sendo constitu&iacute;da por violentas cheias e enormes vazantes. Por fim, pode-se destacar a import&acirc;ncia da vegeta&ccedil;&atilde;o para o processo de infiltra&ccedil;&atilde;o da &aacute;gua da chuva e para minimiza&ccedil;&atilde;o dos efeitos da eros&atilde;o do solo. Ambos os processos, quando viabilizados, contribuem para a harmonia da vaz&atilde;o do rio. Assim, a medida mais eficaz a ser tomada, visando &agrave; conserva&ccedil;&atilde;o da plan&iacute;cie pantaneira e &agrave; preserva&ccedil;&atilde;o de sua grande biodiversidade, &eacute; a conscientiza&ccedil;&atilde;o da sociedade e a organiza&ccedil;&atilde;o de movimentos sociais que exijam o controle do desmatamento e da eros&atilde;o, principalmente nas nascentes dos rios respons&aacute;veis pelo n&iacute;vel das &aacute;guas durante o per&iacute;odo de cheias, o que torna essa alternativa o gabarito da quest&atilde;o.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (2199,445,'E','<p>a constru&ccedil;&atilde;o de barragens, para que o n&iacute;vel das &aacute;guas dos rios seja mantido, sobretudo na estiagem, sem prejudicar os ecossistemas.</p>\r\n','<p>INCORRETA<br />\r\nA constru&ccedil;&atilde;o de barragens n&atilde;o deveria ser exigida, mas proibida, na regi&atilde;o do Pantanal. Recentemente, os impactos negativos das barragens t&ecirc;m estado cada vez mais em foco. Al&eacute;m dos impactos sociais e econ&ocirc;micos, h&aacute; os graves impactos ambientais. Os primeiros impactos ambientais acontecem durante a constru&ccedil;&atilde;o das hidrel&eacute;tricas. Para que a usina funcione &eacute; necess&aacute;rio um reservat&oacute;rio. Sua constru&ccedil;&atilde;o acaba afetando fortemente o clima, a fauna e a flora local. De uma hora para outra, a floresta vira lago. Al&eacute;m do desmatamento, muitas esp&eacute;cies acabam submersas e, consequentemente, morrem, criando uma esp&eacute;cie de limbo. Muitas esp&eacute;cies animais acabam fugindo do seu habitat natural durante a inunda&ccedil;&atilde;o. J&aacute; as esp&eacute;cies aqu&aacute;ticas sofrem um impacto ainda maior. Como a hidrel&eacute;trica &eacute; composta de uma barragem, o fluxo natural dos peixes acaba sendo interrompido drasticamente. A consequ&ecirc;ncia &eacute; a prolifera&ccedil;&atilde;o de determinadas esp&eacute;cies em rela&ccedil;&atilde;o a outras. Soma-se a esse impacto, a eutrofiza&ccedil;&atilde;o das &aacute;guas, que &eacute; o excesso de nutrientes, aumenta a prolifera&ccedil;&atilde;o de micro-organismos, causa comum de polui&ccedil;&atilde;o de &aacute;guas, podendo causar tamb&eacute;m consequ&ecirc;ncias para o homem, como, por exemplo, epidemias.</p>\r\n',0);
