INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (55122,11278,'A','<p>a rela&ccedil;&atilde;o que se estabelece entre as dist&acirc;ncias representadas no mapa e as dist&acirc;ncias reais da superf&iacute;cie cartografada.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A rela&ccedil;&atilde;o entre as dist&acirc;ncias representadas no mapa e as dist&acirc;ncias reais da superf&iacute;cie cartografada &eacute; estabelecida pela escala, n&atilde;o definindo, assim, as coordenadas geogr&aacute;ficas.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (55123,11278,'B','<p>o registro de que os paralelos s&atilde;o verticais e convergem para os polos, e os meridianos s&atilde;o c&iacute;rculos imagin&aacute;rios, horizontais e equidistantes.&nbsp;</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Os meridianos s&atilde;o verticais e convergem para os polos, e os paralelos s&atilde;o c&iacute;rculos imagin&aacute;rios, horizontais e equidistantes.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (55124,11278,'C','<p>a informa&ccedil;&atilde;o de um conjunto de linhas imagin&aacute;rias que permitem localizar um ponto ou acidente geogr&aacute;fico na superf&iacute;cie terrestre.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>As coordenadas geogr&aacute;ficas s&atilde;o um sistema de linhas imagin&aacute;rias tra&ccedil;adas sobre o globo terrestre ou sobre um mapa, localizando cada ponto da superf&iacute;cie da Terra atrav&eacute;s da interse&ccedil;&atilde;o de um paralelo com um meridiano. Assim, t&ecirc;m-se as coordenadas: a latitude e a longitude, sob o princ&iacute;pio da gradua&ccedil;&atilde;o (graus, minutos e segundos), uma vez que paralelos e meridianos s&atilde;o indicados por graus de circunfer&ecirc;ncias.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (55125,11278,'D','<p>a latitude como dist&acirc;ncia em graus entre um ponto e o Meridiano de Greenwich, e a longitude como a dist&acirc;ncia em graus entre um ponto e o Equador.&nbsp;</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A longitude &eacute; a dist&acirc;ncia em graus entre um ponto e o Meridiano de Greenwich, e a latitude &eacute; a dist&acirc;ncia em graus entre um ponto e o Equador.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (55126,11278,'E','<p>a forma de proje&ccedil;&atilde;o cartogr&aacute;fica, usada&nbsp;para navega&ccedil;&atilde;o, onde os meridianos e paralelos distorcem a superf&iacute;cie do planeta.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Coordenadas geogr&aacute;ficas n&atilde;o s&atilde;o uma forma de proje&ccedil;&atilde;o cartogr&aacute;fica, mas mais um recurso cartogr&aacute;fico usado para navega&ccedil;&atilde;o. Al&eacute;m disso, meridianos e paralelos n&atilde;o distorcem a superf&iacute;cie do planeta, mas localizam pontos nela.</p>\r\n',0);
