INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53675,11015,'A','<p>atividade comercial exercida pelos homens que&nbsp;trabalhavam nas minas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O prato t&iacute;pico mineiro ao qual o texto faz refer&ecirc;ncia era comercializado pelos cozinheiros para o consumo dos &ldquo;tropeiros&rdquo;, &ldquo;homens que transportavam gado e mercadoria&rdquo;, e n&atilde;o dos homens que trabalhavam nas minas.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53676,11015,'B','<p>atividade culin&aacute;ria exercida pelos moradores&nbsp;cozinheiros que viviam nas regi&otilde;es das minas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Quem cozinhava e comercializava o feij&atilde;o tropeiro eram os cozinheiros que participavam das tropas e que, portanto, n&atilde;o fixavam moradia na regi&atilde;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53677,11015,'C','<p>atividade mercantil exercida pelos homens que&nbsp;transportavam gado e mercadoria.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>A cria&ccedil;&atilde;o do feij&atilde;o tropeiro, como explicita o texto, est&aacute; ligada &agrave; alimenta&ccedil;&atilde;o preparada pelos &ldquo;cozinheiros das tropas que conduziam o gado.&rdquo; No Brasil, a partir do s&eacute;culo XVIII, ocorreu um significativo aumento demogr&aacute;fico nas regi&otilde;es onde se desenvolveu a atividade das minas, o que fez aumentarem tamb&eacute;m as tropas que circulavam na regi&atilde;o mineradora. O prato t&iacute;pico mineiro ao qual o texto faz refer&ecirc;ncia era comercializado pelos cozinheiros para o consumo dos &ldquo;tropeiros&rdquo;, &ldquo;homens que transportavam gado e mercadoria&rdquo;.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53678,11015,'D','<p>atividade agropecu&aacute;ria exercida pelos tropeiros que&nbsp;necessitavam dispor de alimentos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O feij&atilde;o tropeiro era prepara pelos cozinheiros para que &ldquo;tropeiros&rdquo;, &ldquo;homens que transportavam gado e mercadoria&rdquo; se alimentassem. N&atilde;o se faz associa&ccedil;&atilde;o, portanto, &agrave; cria&ccedil;&atilde;o de gado ou &agrave; agricultura.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53679,11015,'E','<p>atividade mineradora exercida pelos tropeiros no auge&nbsp;da explora&ccedil;&atilde;o do ouro.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Esse termo n&atilde;o se refere aos homens que trabalhavam nas minas, os mineiros, por ser um prato feito para alimenta&ccedil;&atilde;o dos tropeiros.</p>\r\n',0);
