INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53695,11019,'A','<p>impossibilidade de ascens&atilde;o social do negro forro em&nbsp;uma sociedade escravocrata, mesmo sendo&nbsp;alfabetizado.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A biografia de Luiz Gama, resumida no texto-base do item, contraria o que indica a alternativa. O aluno que marca essa op&ccedil;&atilde;o considera apenas o entendimento tradicional da Hist&oacute;ria do Brasil que corrobora a &ldquo;impossibilidade de ascens&atilde;o social do negro forro em uma sociedade escravocrata&rdquo;, que simplifica o forte, mas n&atilde;o infal&iacute;vel sistema de opress&atilde;o social, econ&ocirc;mica e cultural que era a escravid&atilde;o negra no Brasil. Tamb&eacute;m &eacute; preciso considerar o componente importante indicado no texto-base sobre trajet&oacute;ria de Luiz Gama, que se inicia a partir da sua habilidade em &ldquo;ler e escrever&rdquo;.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53696,11019,'B','<p>extrema dificuldade de proje&ccedil;&atilde;o dos intelectuais&nbsp;negros nesse contexto e a utiliza&ccedil;&atilde;o do Direito como&nbsp;canal de luta pela liberdade.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Para escolher o gabarito nessa quest&atilde;o, o aluno deve ser capaz de considerar, como um todo, o sistema escravista brasileiro e, particularmente, relacion&aacute;-lo &agrave; trajet&oacute;ria de vida de Luiz Gama, resumida no texto-base, que indica que a &ldquo;extrema dificuldade&rdquo; enfrentada por ele, como representante dos &ldquo;dos intelectuais negros nesse contexto&rdquo;, come&ccedil;a pela priva&ccedil;&atilde;o de sua liberdade at&eacute; os &ldquo;18 anos de idade&rdquo;, quando &ldquo;conseguiu provas de que havia nascido livre&rdquo;. &Eacute; preciso tamb&eacute;m reconhecer que a sua atua&ccedil;&atilde;o foi deliberadamente dirigida &agrave; &ldquo;utiliza&ccedil;&atilde;o do Direito como canal de luta pela liberdade&rdquo;, j&aacute; que se dedicou por conta pr&oacute;pria ao estudo das leis e se concentrou na causa abolicionista.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53697,11019,'C','<p>&nbsp;rigidez de uma sociedade, assentada na escravid&atilde;o, que&nbsp;inviabilizava os mecanismos de ascens&atilde;o social.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Essa alternativa atrai a aten&ccedil;&atilde;o do aluno ao apontar, com coer&ecirc;ncia, a &ldquo;rigidez de uma sociedade, assentada na escravid&atilde;o&rdquo;. Entretanto, o enunciado exige que seja considerada a biografia de Luiz Gama, que exemplifica, sobretudo, que essa rigidez n&atilde;o era capaz de inviabilizar completamente &ldquo;os mecanismos de ascens&atilde;o social&rdquo;, como por exemplo, a instru&ccedil;&atilde;o formal &agrave; qual Luiz Gama se dedicou desde que aprendeu a ler e escrever at&eacute; a se tornar um advogado abolicionista.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53698,11019,'D','<p>possibilidade de ascens&atilde;o social, viabilizada pelo&nbsp;apoio das elites dominantes, a um mesti&ccedil;o filho de pai&nbsp;portugu&ecirc;s.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Ao escolher essa alternativa, o aluno considera a trajet&oacute;ria de ascens&atilde;o social de Luiz Gama, &ldquo;mesti&ccedil;o filho de pai portugu&ecirc;s&rdquo;, mas n&atilde;o considera que sua biografia indica antes de mais nada uma hist&oacute;ria de luta e conquista, a come&ccedil;ar pela reuni&atilde;o de provas contra a sua escraviza&ccedil;&atilde;o. Assim, as elites dominantes n&atilde;o foram as respons&aacute;veis por, a partir de seu apoio, viabilizar a ascens&atilde;o social de Luiz Gama.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53699,11019,'E','<p>troca de favores entre um representante negro e a elite&nbsp;agr&aacute;ria escravista que lhe outorgara o direito advocat&iacute;cio ao mesmo.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Para escolher essa alternativa, o aluno entende que a liberdade e proje&ccedil;&atilde;o social de Luiz Gama seria resultado de uma &ldquo;troca de favores&rdquo;, considerando, portanto, a viabilidade de intera&ccedil;&otilde;es sociais bilaterais entre um &ldquo;representante negro&rdquo; e a &ldquo;elite agr&aacute;ria&rdquo;. A alternativa se apresenta como plaus&iacute;vel se o aluno entender que a biografia do advogado baiano &eacute; excepcional e marcada pela sua ascens&atilde;o em uma sociedade que o oprimiria. No entanto, a biografia resumida apresentada no texto-base contraria esse entendimento, ao aponta para uma trajet&oacute;ria de supera&ccedil;&atilde;o de obst&aacute;culos que essa &ldquo;elite agr&aacute;ria&rdquo; impunha, a come&ccedil;ar pela priva&ccedil;&atilde;o de sua liberdade; e tamb&eacute;m por que o &ldquo;direito advocat&iacute;cio&rdquo; indicado na alternativa se op&otilde;e ao que se apresenta no texto, que se refere a Luiz Gama como um &ldquo;autodidata, advogado sem diploma&rdquo;.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53730,11026,'A','<p>A ocupa&ccedil;&atilde;o de Portugal pelas tropas francesas e o&nbsp;fechamento das manufaturas portuguesas.&nbsp;</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o &eacute; poss&iacute;vel condicionar o insucesso do projeto industrializante de D. Jo&atilde;o ao suposto fechamento das manufaturas portuguesas por ocasi&atilde;o da invas&atilde;o napole&ocirc;nica em solo lusitano. A transfer&ecirc;ncia da corte significou, a despeito da resist&ecirc;ncia das cortes portuguesa, um isolamento importante da economia da col&ocirc;nia &ndash; que viria a e tornar sede do imp&eacute;rio portugu&ecirc;s em alguns anos.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53731,11026,'B','<p>A depend&ecirc;ncia portuguesa da Inglaterra e o&nbsp;predom&iacute;nio industrial ingl&ecirc;s sobre suas redes de&nbsp;com&eacute;rcio.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Portugal era intimamente associado &agrave; Inglaterra em termos econ&ocirc;micos e diplom&aacute;tico. A revoga&ccedil;&atilde;o do Alvar&aacute; foi tamb&eacute;m acompanhada pela abertura dos portos da col&ocirc;nia &agrave;s na&ccedil;&otilde;es amigas, sobretudo &agrave; Inglaterra, e a produ&ccedil;&atilde;o brasileira n&atilde;o tinha condi&ccedil;&otilde;es de competitividade com os produtos ingleses que passaram a inundar o mercado colonial.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53732,11026,'C','<p>A desconfian&ccedil;a da burguesia industrial colonial diante&nbsp;da chegada da fam&iacute;lia real portuguesa.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o &eacute; poss&iacute;vel atribuir ao per&iacute;odo destacado no texto, o in&iacute;cio do s&eacute;culo XIX, a exist&ecirc;ncia de uma burguesia industrial no Brasil colonial.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53733,11026,'D','<p>O confronto entre a Fran&ccedil;a e a Inglaterra e a posi&ccedil;&atilde;o&nbsp;d&uacute;bia assumida por Portugal no com&eacute;rcio internacional.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Portugal e posicionava marcadamente ao lado da Inglaterra no confronto desse pa&iacute;s com a Fran&ccedil;a, e isso explica inclusive a escola inglesa que a corte portuguesa recebeu ao se transferir para a col&ocirc;nia quanto Napole&atilde;o avan&ccedil;ava sobre o territ&oacute;rio lusitano.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53734,11026,'E','<p>O atraso industrial da col&ocirc;nia provocado pela perda de&nbsp;mercados para as ind&uacute;strias portuguesas.&nbsp;</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Portugal ainda n&atilde;o estava em um est&aacute;gio significativo de industrializa&ccedil;&atilde;o em 1808. Al&eacute;m disso, a transfer&ecirc;ncia da corte significou, a despeito da resist&ecirc;ncia das cortes portuguesa, um isolamento importante da economia da col&ocirc;nia &ndash; que viria a e tornar sede do imp&eacute;rio portugu&ecirc;s em alguns anos.</p>\r\n',0);
