INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59123,12081,'A','<p>a manuten&ccedil;&atilde;o de parte da legisla&ccedil;&atilde;o do Imp&eacute;rio com vistas ao controle da criminalidade urbana.&nbsp;</p>\r\n','<p>INCORRETA<br />\r\nNo caso do c&oacute;digo apresentado acima, a criminalidade na verdade &eacute; vista como algo relacionado a valores culturais das pessoas negras, que antes tinham sido escravizadas, o c&oacute;digo n&atilde;o visa punir a criminalidade, mas manifesta&ccedil;&otilde;es culturais de tais indiv&iacute;duos.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59124,12081,'B','<p>a defesa do retorno do cativeiro e escravid&atilde;o pelos primeiros governos do per&iacute;odo republicano.&nbsp;</p>\r\n','<p>INCORRETA<br />\r\nO c&oacute;digo apresenta uma maneira de controlar a circula&ccedil;&atilde;o, e express&atilde;o cultural de pessoas negras agora n&atilde;o mais escravizadas dentro da Rep&uacute;blica.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59125,12081,'C','<p>o car&aacute;ter disciplinador de uma sociedade industrializada, desejosa de um equil&iacute;brio entre progresso e civiliza&ccedil;&atilde;o.&nbsp;</p>\r\n','<p>INCORRETA<br />\r\nA disciplina&ccedil;&atilde;o prevista no c&oacute;digo penal da Rep&uacute;blica apresentado no texto do item &eacute; de car&aacute;ter racista, no qual controlaria express&otilde;es da popula&ccedil;&atilde;o negra.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59126,12081,'D','<p>a criminaliza&ccedil;&atilde;o de pr&aacute;ticas culturais e a persist&ecirc;ncia de valores que vinculavam certos grupos ao passado de escravid&atilde;o.&nbsp;</p>\r\n','<p>CORRETA<br />\r\nO c&oacute;digo penal da Rep&uacute;blica entendia as pessoas negras como algo a ser controlado, assim como a express&atilde;o de sua cultura pr&oacute;pria, criminalizando sua maneira de ser e estar no mundo.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59127,12081,'E','<p>o poder do regime escravista, que mantinha os negros como categoria social inferior, discriminada e segregada.&nbsp;</p>\r\n','<p>INCORRETA<br />\r\nO c&oacute;digo apresentado &eacute; um ran&ccedil;o de regime escravista, que continuou de certa forma ap&oacute;s a liberta&ccedil;&atilde;o com o racismo, e com leis como essa, para conter, controlar e disciplinar o povo negro.</p>\r\n',0);
