INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59128,12082,'A','<p>A distribui&ccedil;&atilde;o equilibrada do poder.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O texto de Val&eacute;ry, como aponta o enunciado, implica um desequil&iacute;brio no poder pol&iacute;tico em um primeiro momento, uma vez que um grupo social impede outro de se ocupar do que tamb&eacute;m lhe diz respeito.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59129,12082,'B','<p>O impedimento da participa&ccedil;&atilde;o popular.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O segundo momento do poder pol&iacute;tico, como aponta o texto, implica ampla participa&ccedil;&atilde;o popular, mesmo quando a popula&ccedil;&atilde;o n&atilde;o &eacute; educada sobre o que est&aacute; decidindo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59130,12082,'C','<p>&nbsp;O controle das decis&otilde;es por uma minoria.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Nos dois momentos pol&iacute;ticos caracterizados por Val&eacute;ry, apenas uma minoria est&aacute; de fato no controle das decis&otilde;es. Em um primeiro momento, isso se daria de forma autorit&aacute;ria &ndash; impedindo a participa&ccedil;&atilde;o pol&iacute;tica; em um segundo momento, teria ares de democracia &ndash; mas apenas uma minoria compeliria as pessoas desinformadas a decidirem X ou Y (via propaganda, por exemplo).</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59131,12082,'D','<p>A valoriza&ccedil;&atilde;o das opini&otilde;es mais competentes.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>No segundo momento pol&iacute;tico caracterizado por Val&eacute;ry, as opini&otilde;es &ldquo;mais competentes&rdquo; valem o mesmo tanto que as das pessoas que n&atilde;o entendem sobre o que decidem.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59132,12082,'E','<p>A sistematiza&ccedil;&atilde;o dos processos decis&oacute;rios.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Val&eacute;ry n&atilde;o trata da sistematiza&ccedil;&atilde;o dos processos decis&oacute;rios. Ambos os momentos descritos podem ocorrer de forma n&atilde;o sistematizada.</p>\r\n',0);
