INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59168,12090,'A','<p>instrumento de garantia da cidadania, porque por meio dela os cidad&atilde;os passam a pensar e agir de acordo com valores coletivos.</p>\r\n','<p>CORRETA.</p>\r\n\r\n<p>A alternativa &eacute; a &uacute;nica que contempla a ideia, no texto, de que &ldquo;cada grupo social se organize sentindo-se respons&aacute;vel por todos&rdquo;.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59169,12090,'B','<p>mecanismo de cria&ccedil;&atilde;o de direitos humanos, porque &eacute; da natureza do homem ser &eacute;tico e virtuoso.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>A alternativa &eacute; autocontradit&oacute;ria; se o homem fosse &eacute;tico e virtuoso por natureza, n&atilde;o seria necess&aacute;ria a cria&ccedil;&atilde;o de direitos formais para garantir o agir correto.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59170,12090,'C','<p>meio para resolver os conflitos sociais no cen&aacute;rio da globaliza&ccedil;&atilde;o, pois, a partir do entendimento do que &eacute; efetivamente a &eacute;tica, a pol&iacute;tica internacional se realiza.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>Apesar de a alternativa reverberar algumas das afirma&ccedil;&otilde;es do texto, a no&ccedil;&atilde;o de que a pol&iacute;tica internacional n&atilde;o se realiza sem a resolu&ccedil;&atilde;o dos conflitos &eacute; err&ocirc;nea, j&aacute; que esses conflitos se d&atilde;o propriamente em um cen&aacute;rio pol&iacute;tico internacional e real.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59171,12090,'D','<p>par&acirc;metro para assegurar o exerc&iacute;cio pol&iacute;tico primando pelos interesses e a&ccedil;&atilde;o privada dos cidad&atilde;os.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>A alternativa ignora o apelo social do texto ao apoiar-se na no&ccedil;&atilde;o de a&ccedil;&atilde;o privada por parte dos cidad&atilde;os.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59172,12090,'E','<p>aceita&ccedil;&atilde;o de valores universais impl&iacute;citos numa sociedade que busca dimensionar sua vincula&ccedil;&atilde;o &agrave; outras sociedades.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>Se a &eacute;tica &ldquo;sup&otilde;e ainda que cada grupo social se organize sentindo-se respons&aacute;vel&rdquo;, isso implica a possibilidade de que cada grupo se paute por valores distintos e, portanto, n&atilde;o universais.</p>\r\n',0);
