INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59138,12084,'A','<p>&agrave; baixa representatividade pol&iacute;t&iacute;ca de grupos organizados que defendem os direitos de cidadania dos homossexuais.</p>\r\n','<p>INCORRETA<br />\r\nAinda que de fato haja uma significativa baixa representatividade pol&iacute;tica de grupos LGBTs, o texto associa a viol&ecirc;ncia do presente ao seu percurso hist&oacute;rico de intoler&acirc;ncia.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59139,12084,'B','<p>&agrave; fal&ecirc;ncia da democracia no pa&iacute;s, que torna impeditiva a divulga&ccedil;&atilde;o de estat&iacute;sticas relacionadas &agrave; viol&ecirc;ncia contra homossexuais.</p>\r\n','<p>INCORRETA<br />\r\nO texto traz como dado estat&iacute;stico o n&uacute;mero de pessoas LGBT assassinadas no Brasil em 2009, o que confirma a liberdade com que essa informa&ccedil;&atilde;o circula.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59140,12084,'C','<p>&agrave; Constitui&ccedil;&atilde;o de 1988, que exclui do tecido social os homossexuais, al&eacute;m de impedi-los de exercer seus direitos pol&iacute;ticos.</p>\r\n','<p>INCORRETA<br />\r\nO texto Constitucional de 1988 est&aacute; calcado no princ&iacute;pio da Isonomia que assegura a igualdade do(a) cidad&atilde;o(&atilde;) brasileiro independente de seus marcadores pol&iacute;ticos.&nbsp;</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59141,12084,'D','<p>a um passado hist&oacute;rico marcado pela demoniza&ccedil;&atilde;o do corpo e por formas recorrentes de tabus e intoler&acirc;ncia.</p>\r\n','<p>CORRETA<br />\r\nA quest&atilde;o tenta aproximar o hist&oacute;rico de intoler&acirc;ncia &agrave; homossexualidade com o atual quadro de viol&ecirc;ncia e mortalidade que essa popula&ccedil;&atilde;o est&aacute; exposta como sintoma do ainda presente preconceito direcionado &agrave; orienta&ccedil;&atilde;o sexual.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59142,12084,'E','<p>a uma pol&iacute;tica eug&ecirc;nica desenvolvida pelo Estado, justificada a partir dos posicionamentos de correntes filos&oacute;fico-cient&iacute;ficas.</p>\r\n','<p>INCORRETA<br />\r\nO Estado, ainda que omisso em situa&ccedil;&otilde;es de homofobia flagrante, em tese, preocupa-se em investigar, punir e evitar crimes de &oacute;dio contra a popula&ccedil;&atilde;o LGBT e outras minorias pol&iacute;ticas.&nbsp;</p>\r\n',0);
