INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59173,12091,'A','<p>qualidade total e estabilidade no trabalho.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A(o) aluna(o) que marcou essa alternativa deve compreender que tornar os processos mais eletr&ocirc;nicos e mecanizados acarreta em aumento do desemprego. E a ideia de qualidade n&atilde;o necessariamente &eacute; consequ&ecirc;ncia de servi&ccedil;os tecnol&oacute;gicos.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59174,12091,'B','<p>pleno emprego e enfraquecimento dos sindicatos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aumento do desemprego, e uma fragiliza&ccedil;&atilde;o das leis trabalhistas deve acarretar em fortalecimento de sindicatos na busca de direitos. As tecnologias s&atilde;o respons&aacute;veis por uma demiss&atilde;o de trabalhadores que podem se tornar obsoletos, e &eacute; preciso que a(o) aluna(o) compreenda tal consequ&ecirc;ncia.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59175,12091,'C','<p>diminui&ccedil;&atilde;o dos custos e inseguran&ccedil;a no emprego.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>A manuten&ccedil;&atilde;o de tecnologias &eacute; mais barata que manter um(a) trabalhador(a), que al&eacute;m de seu sal&aacute;rio recebe outros direitos, e isso &eacute; algo central na ideia de mecaniza&ccedil;&atilde;o do trabalho, substituindo a(o) empregado(a) por uma tecnologia que contemple sua fun&ccedil;&atilde;o, acarretando em um grandes chances de demiss&atilde;o.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59176,12091,'D','<p>responsabilidade social e redu&ccedil;&atilde;o do desemprego.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A(o) aluna(o) que marcou est&aacute; alternativa deve ter compreendido que um atendimento mais r&aacute;pido pode ser poss&iacute;vel devido a substitui&ccedil;&atilde;o por caixas eletr&ocirc;nicos pode gerar uma falsa ideia de benef&iacute;cio social, pela rapidez no atendimento. Contudo deve identificar que para isso ocorre um grande n&uacute;mero de demiss&otilde;es de funcion&aacute;rias(os) que tiveram suas fun&ccedil;&otilde;es substitu&iacute;das por m&aacute;quinas.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (59177,12091,'E','<p>maximiza&ccedil;&atilde;o dos lucros e aparecimento de empregos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A(o) aluna(o) deve ser capaz de identifica que existe de fato um decl&iacute;nio nos gastos das empresas com a mecaniza&ccedil;&atilde;o e o uso das tecnologias, por poder demitir v&aacute;rias(os) funcion&aacute;rias(os), e nesse sentido um aumento de seus lucros, e assim h&aacute; um crescimento no desemprego.</p>\r\n',0);
