INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6136,1261,'A','<p>antibi&oacute;tico contra o v&iacute;rus da febre amarela, soro antiof&iacute;dico caso seja picado por uma cobra e vacina contra a leptospirose.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa n&atilde;o compreende que n&atilde;o se utiliza antibi&oacute;tico para o tratamento de v&iacute;rus e que n&atilde;o h&aacute; vacina para a leptospirose.&nbsp;</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6137,1261,'B','<p>vacina contra o v&iacute;rus da febre amarela, soro antiof&iacute;dico caso seja picado por uma cobra e antibi&oacute;tico caso entre em contato com a <em>Leptospira</em> sp.</p>\r\n','<p>CORRETA.<br />\r\nA imuniza&ccedil;&atilde;o contra o v&iacute;rus da febre amarela &eacute; realizada por meio da aplica&ccedil;&atilde;o de vacina. Quando ocorre a picada de cobra pe&ccedil;onhenta, o procedimento mais adequado &eacute; administrar soro antiof&iacute;dico, pois o soro &eacute; constitu&iacute;do por uma grande quantidade de anticorpos espec&iacute;ficos, que funcionam como um ant&iacute;doto ao veneno da cobra. A leptospirose &eacute; causada pela bact&eacute;ria <em>Leptospira sp</em>., e o uso de antibi&oacute;ticos &eacute; o procedimento que deve ser feito para combater essa doen&ccedil;a.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6138,1261,'C','<p>soro contra o v&iacute;rus da febre amarela, antibi&oacute;tico caso seja picado por uma cobra e soro contra toxinas bacterianas.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa n&atilde;o compreende que n&atilde;o h&aacute; soro para a febre amarela; para o caso de picadas de cobras pe&ccedil;onhentas o tratamento mais adequado &eacute; a administra&ccedil;&atilde;o de soro antiof&iacute;dico e para tratar infec&ccedil;&otilde;es bacterianas deve-se fazer uso de antibi&oacute;ticos.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6139,1261,'D','<p>antibi&oacute;tico ou soro, tanto contra o v&iacute;rus da febre amarela como para veneno de cobras, e vacina contra a leptospirose.</p>\r\n','<p>INCORRETA<br />\r\nO aluno que seleciona essa alternativa n&atilde;o compreende que antibi&oacute;tico n&atilde;o &eacute; o tratamento adequado nem para o tratamento do v&iacute;rus de febre amarela e nem para picada de cobra pe&ccedil;onhenta. O soro &eacute; indicado somente para picada de cobra, e n&atilde;o para o caso de febre amarela. Al&eacute;m disso, n&atilde;o h&aacute; vacina contra leptospirose.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6140,1261,'E','<p>soro antiof&iacute;dico e antibi&oacute;tico contra a <em>Leptospira </em>sp e vacina contra a febre amarela caso entre em contato com o v&iacute;rus causador da doen&ccedil;a.</p>\r\n','<p>INCORRETA<br />\r\nO aluno que seleciona essa alternativa n&atilde;o compreende que soro antiof&iacute;dico n&atilde;o &eacute; adequado contra a leptospirose. Al&eacute;m disso, a vacina &eacute; uma medida usada para induzir a imuniza&ccedil;&atilde;o do organismo, devendo ser aplicada antes de haver o contato com o v&iacute;rus da febre amarela.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (3988,811,'A','<p>reflex&atilde;o.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>A reflex&atilde;o &eacute; a respons&aacute;vel por &ldquo;burlar&rdquo; a curvatura da Terra, fazendo os raios retornarem ao ch&atilde;o ap&oacute;s serem refletidos na ionosfera.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (3989,811,'B','<p>refra&ccedil;&atilde;o.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Entende que h&aacute; uma mudan&ccedil;a no meio de propaga&ccedil;&atilde;o da onda, e portanto h&aacute; refra&ccedil;&atilde;o, por&eacute;m n&atilde;o compreende que o fen&ocirc;meno n&atilde;o ajuda na situa&ccedil;&atilde;o descrita.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (3990,811,'C','<p>difra&ccedil;&atilde;o.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Conhece o fen&ocirc;meno da difra&ccedil;&atilde;o como o respons&aacute;vel, em v&aacute;rias situa&ccedil;&otilde;es, por fazer com que uma onda chegue a lugares inacess&iacute;veis. No entanto, n&atilde;o interpreta bem a quest&atilde;o e n&atilde;o percebe que, nesse caso, a difra&ccedil;&atilde;o n&atilde;o est&aacute; presente.</p>\r\n',0);
