INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (4018,817,'A','<p>permite a entrada de calor do ambiente externo para o interior da seringa.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Acredita que a fervura est&aacute; ligada somente &agrave; absor&ccedil;&atilde;o de calor, sem considerar tamb&eacute;m a temperatura de ebuli&ccedil;&atilde;o.</p>\r\n',0);
