INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (4020,817,'C','<p>produz um aumento de volume que aumenta o ponto de ebuli&ccedil;&atilde;o da &aacute;gua.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Relaciona uma informa&ccedil;&atilde;o verdadeira (aumento de volume) com uma explica&ccedil;&atilde;o que, na verdade, n&atilde;o ocorre (aumento do ponto de ebuli&ccedil;&atilde;o).</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (4021,817,'D','<p>proporciona uma queda de press&atilde;o no interior da seringa que diminui o ponto de ebuli&ccedil;&atilde;o da &aacute;gua.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O aumento de volume provoca uma diminui&ccedil;&atilde;o na press&atilde;o, de acordo com a rela&ccedil;&atilde;o PV=nRT. Essa diminui&ccedil;&atilde;o provoca uma diminui&ccedil;&atilde;o no ponto de ebuli&ccedil;&atilde;o, como pode ser comprovado olhando um diagrama de fases da &aacute;gua.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (4022,817,'E','<p>possibilita uma diminui&ccedil;&atilde;o da densidade da &aacute;gua que facilita sua ebuli&ccedil;&atilde;o.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Relaciona o aumento do volume com uma diminui&ccedil;&atilde;o da densidade.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6171,1268,'A','<p>diminua devido &agrave; utiliza&ccedil;&atilde;o de caldeiras por ind&uacute;strias metal&uacute;rgicas.</p>\r\n','<p>INCORRETA<br />\r\nO aluno que selecionar essa alternativa n&atilde;o compreende que haver&aacute; aumento do consumo de energia el&eacute;trica</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6172,1268,'B','<p>aumente devido ao bloqueio da luz do sol pelos gases do efeito estufa.</p>\r\n','<p>INCORRETA<br />\r\nO aluno que selecionar essa alternativa n&atilde;o compreende que o consumo de energia el&eacute;trica ir&aacute; aumentar devido &agrave; necessidade de maior refrigera&ccedil;&atilde;o de ind&uacute;strias e resid&ecirc;ncias.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6173,1268,'C','<p>diminua devido &agrave; n&atilde;o necessidade de aquecer a &aacute;gua utilizada em ind&uacute;strias.</p>\r\n','<p>&nbsp;INCORRETA<br />\r\nO aluno que selecionar essa alternativa n&atilde;o compreende que haver&aacute; aumento do consumo de energia el&eacute;trica, e n&atilde;o a sua diminui&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6174,1268,'D','<p>aumente devido &agrave; necessidade de maior refrigera&ccedil;&atilde;o de ind&uacute;strias e resid&ecirc;ncias.</p>\r\n','<p>CORRETA<br />\r\nO efeito estufa e as &ldquo;ilhas de calor&rdquo; provocam aumento da temperatura ambiente local. Para adequar a temperatura ao bem-estar das pessoas, aparelhos de ar-condicionado e de refrigera&ccedil;&atilde;o devem ser usados, elevando o consumo de energia el&eacute;trica.</p>\r\n',1);
