INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6156,1265,'A','<p>aumento da salinidade, em que a &aacute;gua do solo atinge uma concentra&ccedil;&atilde;o de sais maior que a das c&eacute;lulas das ra&iacute;zes das plantas, impedindo, assim, que a &aacute;gua seja absorvida.</p>\r\n','<p>CORRETA.</p>\r\n\r\n<p>O aumento da salinidade do solo torna-o hipert&ocirc;nico em rela&ccedil;&atilde;o &agrave; concentra&ccedil;&atilde;o das c&eacute;lulas radiculares. Em consequ&ecirc;ncia, as ra&iacute;zes perdem a capacidade de absorver &aacute;gua do meio.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6157,1265,'B','<p>aumento da salinidade, em que o solo atinge um n&iacute;vel muito baixo de &aacute;gua, e as plantas n&atilde;o t&ecirc;m for&ccedil;a de suc&ccedil;&atilde;o para absorver a &aacute;gua.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>N&atilde;o compreende que o aumento da salinidade do solo aumenta a reten&ccedil;&atilde;o de &aacute;gua no mesmo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6158,1265,'C','<p>diminui&ccedil;&atilde;o da salinidade, que atinge um n&iacute;vel em que as plantas n&atilde;o t&ecirc;m for&ccedil;a de suc&ccedil;&atilde;o, fazendo com que a &aacute;gua n&atilde;o seja absorvida.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>N&atilde;o compreende que a presen&ccedil;a de sais na solu&ccedil;&atilde;o do solo aumenta a salinidade do mesmo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (6159,1265,'D','<p>aumento da salinidade, que atinge um n&iacute;vel em que as plantas t&ecirc;m muita suda&ccedil;&atilde;o, n&atilde;o tendo for&ccedil;a de suc&ccedil;&atilde;o para super&aacute;-la.</p>\r\n','<p>INCORRETA.</p>\r\n\r\n<p>Compreende que a suda&ccedil;&atilde;o ou guta&ccedil;&atilde;o das plantas ocorre quando o solo est&aacute; bem suprido de &aacute;gua. Contudo, n&atilde;o compreende que o aumento da salinidade do solo torna-o hipert&ocirc;nico em rela&ccedil;&atilde;o &agrave; concentra&ccedil;&atilde;o das c&eacute;lulas radiculares, fazendo com que a &aacute;gua n&atilde;o seja absorvida pelas ra&iacute;zes das plantas.</p>\r\n',0);
