INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1768,359,'A','<p>IV, I, II e III.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que selecionar essa alternativa n&atilde;o compreende que o &aacute;cido carb&ocirc;nico est&aacute; nos reagentes, e n&atilde;o sendo formado. Al&eacute;m disso, o aluno acredita que o &aacute;cido carb&ocirc;nico seja um g&aacute;s.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1769,359,'B','<p>I, IV, III e II.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que selecionar essa alternativa n&atilde;o compreende que compostos i&ocirc;nicos j&aacute; possuem &iacute;ons em sua estrutura, sofrendo um processo de dissocia&ccedil;&atilde;o, e n&atilde;o de ioniza&ccedil;&atilde;o. Al&eacute;m disso, o aluno n&atilde;o compreende que os &aacute;cidos s&atilde;o compostos moleculares e os &iacute;ons s&atilde;o formados, n&atilde;o apenas separados, pois n&atilde;o existiam em sua estrutura inicial.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1770,359,'C','<p>IV, III, I e II.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que selecionar essa alternativa n&atilde;o compreende que os &aacute;cidos s&atilde;o compostos moleculares e que os &iacute;ons s&atilde;o formados, e n&atilde;o apenas separados, pois n&atilde;o existiam em sua estrutura inicial. O aluno n&atilde;o compreende, tamb&eacute;m, o processo de dissocia&ccedil;&atilde;o nem o de hidr&oacute;lise. Acredita que o &acirc;nion bicarbonato, liberado na dissocia&ccedil;&atilde;o, esteja atuando como &aacute;cido pelo fato de apresentar um H em sua estrutura.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1771,359,'D','<p>I, IV, II e III.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que selecionar essa alternativa n&atilde;o compreende que compostos i&ocirc;nicos j&aacute; possuem &iacute;ons em sua estrutura, sofrendo um processo de dissocia&ccedil;&atilde;o, e n&atilde;o de ioniza&ccedil;&atilde;o.</p>\r\n\r\n<p>O aluno n&atilde;o compreende, tamb&eacute;m, que os &aacute;cidos s&atilde;o compostos moleculares e que os &iacute;ons s&atilde;o formados, e n&atilde;o apenas separados, pois n&atilde;o existiam em sua estrutura inicial. Al&eacute;m disso, n&atilde;o compreende que o &aacute;cido carb&ocirc;nico est&aacute; nos reagentes, e n&atilde;o sendo formado, e acredita que esse composto seja um g&aacute;s.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1772,359,'E','<p>IV, I, III e II.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>I. &nbsp; &nbsp; NaHCO<sub>3</sub>&nbsp;&rarr; Na<sup>+</sup> + HCO<sub>3</sub><sup>-</sup></p>\r\n\r\n<p>Dissocia&ccedil;&atilde;o: separa&ccedil;&atilde;o do c&aacute;tion e do &acirc;nion j&aacute; existentes na estrutura de um sal, que &eacute; um composto i&ocirc;nico.</p>\r\n\r\n<p>II. &nbsp; &nbsp;H<sub>2</sub>CO<sub>3</sub> &nbsp;H<sub>2</sub>O + CO<sub>2</sub></p>\r\n\r\n<p>Libera&ccedil;&atilde;o de g&aacute;s: o &aacute;cido carb&ocirc;nico, que &eacute; um composto inst&aacute;vel, decomp&otilde;e-se em &aacute;gua e di&oacute;xido de carbono, que &eacute; um g&aacute;s.</p>\r\n\r\n<p>III. &nbsp; HCO<sub>3</sub><sup>-</sup> + H<sub>2</sub>O &nbsp;H<sub>2</sub>CO<sub>3</sub></p>\r\n\r\n<p>Forma&ccedil;&atilde;o de &aacute;cido: o &acirc;nion bicarbonato sofre hidr&oacute;lise em &aacute;gua, formando o &aacute;cido correspondente.</p>\r\n\r\n<p>IV. &nbsp; H<sub>3</sub>A &nbsp;3H<sup>+</sup> + A<sup>-</sup></p>\r\n\r\n<p>Ioniza&ccedil;&atilde;o: um &aacute;cido, que &eacute; um composto molecular, forma c&aacute;tions e &acirc;nions.</p>\r\n',1);
