INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (40674,8459,'A','<p>insatisfat&oacute;ria, pois a rea&ccedil;&atilde;o apresentada n&atilde;o permite&nbsp;que a energia presente no meio externo seja absorvida&nbsp;pelo sistema para ser utilizada posteriormente.</p>\r\n','<p>INCORRETA<br />\r\nN&atilde;o compreende que a rea&ccedil;&atilde;o apresentada consiste em um fen&ocirc;meno endot&eacute;rmico, que ocorre com absor&ccedil;&atilde;o de calor.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (40675,8459,'B','<p>insatisfat&oacute;ria, uma vez que h&aacute; forma&ccedil;ao de gases&nbsp;poluentes e com potencial poder explosivo, tornando-a&nbsp;uma rea&ccedil;ao perigosa e de dif&iacute;cil controle.</p>\r\n','<p>INCORRETA<br />\r\nN&atilde;o compreende que o mon&oacute;xido de carbono e o g&aacute;s hidrog&ecirc;nio formados n&atilde;o s&atilde;o gases poluentes. Possivelmente confunde o CO com o CO<sub>2</sub>.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (40676,8459,'C','<p>insatisfat&oacute;ria, uma vez que h&aacute; forma&ccedil;&atilde;o de g&aacute;s CO, que&nbsp;n&atilde;o possui conte&uacute;do energ&eacute;tico pass&iacute;vel de ser aproveitado posteriormente e &eacute; considerado um g&aacute;s poluente.</p>\r\n','<p>INCORRETA<br />\r\nN&atilde;o compreende que o mon&oacute;xido de carbono pode sofrer combust&atilde;o, gerando energia, e nem que esse g&aacute;s n&atilde;o &eacute; um poluente.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (40677,8459,'D','<p>satisfat&oacute;ria, uma vez que a rea&ccedil;&atilde;o direta ocorre com&nbsp;absor&ccedil;&atilde;o de calor e promove a forma&ccedil;&atilde;o das subst&acirc;ncias combust&iacute;veis que poder&atilde;o ser utilizadas posteriormente para obten&ccedil;&atilde;o de energia e realiza&ccedil;&atilde;o de&nbsp;trabalho &uacute;til.</p>\r\n','<p>CORRETA<br />\r\nA rea&ccedil;&atilde;o apresentada consiste em um fen&ocirc;meno qu&iacute;mico endot&eacute;rmico, visto que o calo est&aacute; nos reagentes, sendo consumido na rea&ccedil;&atilde;o. Considerando essa rea&ccedil;&atilde;o como potencial mecanismo para o aproveitamento posterior da energia solar, verifica-se que se trata de uma estrat&eacute;gia satisfat&oacute;ria, uma vez que a rea&ccedil;&atilde;o direta ocorre com absor&ccedil;&atilde;o de calor promovendo a forma&ccedil;&atilde;o de subst&acirc;ncias combust&iacute;veis (mon&oacute;xido de carbono e g&aacute;s hidrog&ecirc;nio), as quais poder&atilde;o ser utilizadas posteriormente para obten&ccedil;&atilde;o de energia e realiza&ccedil;&atilde;o de trabalho.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (40678,8459,'E','<p>satisfat&oacute;ria, uma vez que a rea&ccedil;&atilde;o direta ocorre com&nbsp;libera&ccedil;&atilde;o de calor havendo, ainda, a forma&ccedil;&atilde;o das&nbsp;subst&acirc;ncias combust&iacute;veis que poder&atilde;o ser utilizadas&nbsp;posteriormente para obten&ccedil;&atilde;o de energia e realiza&ccedil;&atilde;o&nbsp;de trabalho &uacute;til.</p>\r\n','<p>INCORRETA<br />\r\nN&atilde;o compreende que a rea&ccedil;&atilde;o apresentada consiste em um fen&ocirc;meno endot&eacute;rmico, que ocorre com absor&ccedil;&atilde;o de calor.</p>\r\n',0);
