INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (4045,822,'C','<p>&aacute;gua exercer&aacute; uma for&ccedil;a na escultura proporcional a sua massa, e para cima. Esta for&ccedil;a se somar&aacute; &agrave; for&ccedil;a que os trabalhadores fazem para anular a a&ccedil;&atilde;o da for&ccedil;a peso da escultura.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa n&atilde;o se lembra da equa&ccedil;&atilde;o para o empuxo, apesar de saber que essa &eacute; a for&ccedil;a que agir&aacute; na escultura.</p>\r\n',0);
