INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12498,2597,'A','<p>o combate &agrave; AIDS e a melhoria do ensino universit&aacute;rio.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Embora o combate &agrave; AIDS seja uma das metas, a melhoria proposta pela Organiza&ccedil;&atilde;o das Na&ccedil;&otilde;es Unidas &eacute; na educa&ccedil;&atilde;o fundamental e n&atilde;o no ensino superior. O aluno pode sentir-se inclinado a marcar esse distrator devido a uma das propostas estar correta.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12499,2597,'B','<p>a redu&ccedil;&atilde;o da mortalidade adulta e a cria&ccedil;&atilde;o de parcerias globais.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Al&eacute;m da redu&ccedil;&atilde;o de mortalidade a ser reduzida ser a infantil, as parcerias globais a serem desenvolvidas s&atilde;o relacionadas ao desenvolvimento, portanto, ambas alternativas oferecidas pelo distrator est&atilde;o incorretas. Esse distrator s&oacute; ser&aacute; escolhido se o aluno n&atilde;o compreender o vocabul&aacute;rio na imagem, principalmente as palavras &ldquo;child&rdquo; (crian&ccedil;a) e &ldquo;development&rdquo; (desenvolvimento).</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12500,2597,'C','<p>a promo&ccedil;&atilde;o da igualdade de g&ecirc;neros e a erradica&ccedil;&atilde;o da pobreza.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Tanto a promo&ccedil;&atilde;o da igualdade de g&ecirc;neros quanto a erradica&ccedil;&atilde;o da pobreza s&atilde;o itens contidos na imagem, sendo, respectivamente, o quadro 3 e o quadro 1.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12501,2597,'D','<p>a parceria global para o desenvolvimento e a valoriza&ccedil;&atilde;o das crian&ccedil;as.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Embora as parcerias para o desenvolvimento sejam uma das metas (meta 8), a valoriza&ccedil;&atilde;o proposta &eacute; a das mulheres. O que se pretende em rela&ccedil;&atilde;o &agrave;s crian&ccedil;as &eacute; a redu&ccedil;&atilde;o da mortalidade. O aluno pode sentir-se inclinado a marcar esse distrator devido a uma das propostas estar correta.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12502,2597,'E','<p>a garantia da sustentabllidade ambiental e o combate ao trabalho infantil.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Embora a sustentabilidade ambiental seja uma das metas, o combate ao trabalho infantil n&atilde;o &eacute; citado. O aluno pode marcar este distrator caso correlacione a outra com seus conhecimentos de mundo, lembrando-se do combate ao trabalho infantil como um problema global a ser erradicado, embora n&atilde;o seja uma pauta da ONU.</p>\r\n',0);
