INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12713,2646,'D','<p>A introdu&ccedil;&atilde;o do guarani nas escolas potencializou a difus&atilde;o da l&iacute;ngua, mas &eacute;&nbsp; necess&aacute;rio que haja uma postura &eacute;tica em seu ensino.</p>\r\n','',0);
