INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12727,2650,'A','<p>falta de continuidade do ensino do guarani nos programas escolares.</p>\r\n','<p>INCORRETA<br />\r\nO aluno pode vir a marcar erroneamente a op&ccedil;&atilde;o movido pela cita&ccedil;&atilde;o do termo &ldquo;continuidade&rdquo; no subt&iacute;tulo (&ldquo;Continuidad, no continuismo&rdquo;) e/ou pela defesa expressa do ensino do guarani presente no &uacute;ltimo par&aacute;grafo (&ldquo;Cualquier intento de marginaci&oacute;n del guaran&iacute; en la educaci&oacute;n paraguaya merece la m&aacute;s viva y decidida protesta&rdquo;). Entretanto, o trecho &ldquo;La inclusi&oacute;n de la lengua guaran&iacute; en el proceso de educaci&oacute;n escolar fue sin duda un avance de la Reforma Educativa&rdquo; comprova que o guaran&iacute; n&atilde;o deixou de ser ensinado nas escolas.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12728,2650,'B','<p>preconceito existente contra o guarani, principalmente nas escolas.</p>\r\n','<p>INCORRETA<br />\r\nO preconceito de fato &eacute; mencionado no segundo par&aacute;grafo (&ldquo;algunos castellanohablantes perdieron el miedo al guaran&iacute; y superaron los prejuicios en contra de &eacute;l&rdquo;), o que pode convencer o estudante a marcar esta op&ccedil;&atilde;o. Entretanto, essa mesma alternativa explica justamente que esses preconceitos foram superados por muitas pessoas.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12729,2650,'C','<p>esperan&ccedil;a acumulada na reforma educativa da educa&ccedil;&atilde;o m&eacute;dia.</p>\r\n','<p>INCORRETA<br />\r\nTanto a express&atilde;o &ldquo;educa&ccedil;&atilde;o m&eacute;dia&rdquo; quanto &ldquo;esperan&ccedil;a&rdquo; aparecem no segundo par&aacute;grafo (&ldquo;Dejar fuera de la Educaci&oacute;n Media al guaran&iacute; seria echar por la borda tanto trabajo realizado, tanta esperanza acumulada&rdquo;) podendo ludibriar um respondente apressado. Essa afirmativa, entretanto, n&atilde;o explica por que houve o processo de mobiliza&ccedil;&atilde;o social.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12730,2650,'D','<p>inclus&atilde;o e perman&ecirc;ncia do ensino do guarani nas escolas.</p>\r\n','<p>CORRETA<br />\r\nO trecho &ldquo;Gracias precisamente a los programas escolares, aun en contextos urbanos, el biling&uuml;ismo ha sido potenciado&rdquo; mostra que a inclus&atilde;o do guarani nos contextos escolares foi fundamental para garantir o processo de valoriza&ccedil;&atilde;o desse idioma no pa&iacute;s. No final do primeiro par&aacute;grafo e no &uacute;ltimo, sobretudo, o autor defende a inclus&atilde;o e perman&ecirc;ncia do bilinguismo, por uma quest&atilde;o &eacute;tica, n&atilde;o de imposi&ccedil;&atilde;o.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12731,2650,'E','<p>continu&iacute;smo do ensino do castelhano nos centros urbanos.</p>\r\n','<p>INCORRETA<br />\r\nO texto n&atilde;o valoriza somente o castelhano, mas o continu&iacute;smo de ambas as l&iacute;nguas (castelhano e guarani) nos cen&aacute;rios escolares e urbanos, por quest&otilde;es &eacute;ticas e, principalmente, culturais. &nbsp;O estudante pode ser guiado a marcar essa alternativa erroneamente pelo t&iacute;tulo do texto ou pela rela&ccedil;&atilde;o estabelecida entre as duas l&iacute;nguas ao longo do texto.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12744,2654,'A','<p>consultar as autoridades para verificar a possibilidade de viagem.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno pode escolher a op&ccedil;&atilde;o baseado em experi&ecirc;ncias pessoais, j&aacute; que isso n&atilde;o est&aacute; expresso na alternativa. O texto s&oacute; menciona a obrigatoriedade de um passaporte e de vacinas para os animais que viajar&atilde;o com seus donos. N&atilde;o menciona uma consulta com autoridades que devem aprovar ou n&atilde;o a viagem.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12745,2654,'B','<p>ter um certificado especial tirado em outubro de 2004.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A men&ccedil;&atilde;o ao ano de 2004 pode atrair o olhar de um respondente apressado. Entretanto, essa informa&ccedil;&atilde;o se refere &agrave; data a partir da qual passa a valer a exig&ecirc;ncia de passaporte para os animais que viajar&atilde;o com seus donos. O certificado pode ter sido tirado antes ou ap&oacute;s essa data, mas sua apresenta&ccedil;&atilde;o come&ccedil;aria a ser exigida a partir de 2004.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12746,2654,'C','<p>tirar o passaporte do animal e logo vacin&aacute;-lo.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Tanto o passaporte quanto a vacina s&atilde;o mencionados no texto &ndash; mas em outra ordem. A presen&ccedil;a dos termos, por&eacute;m, pode confundir o estudante. O trecho &ldquo;en Espa&ntilde;a los animales deben haber sido vacinados contra la rabia <strong>antes de</strong> su due&ntilde;o solicitar la documentaci&oacute;n&rdquo; comprova que a vacina deve ser providenciada antes do passaporte do animal.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12747,2654,'D','<p>vacinar o animal contra todas as doen&ccedil;as.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno apressado pode ser atra&iacute;do pelo verbo &ldquo;vacinar&rdquo; e n&atilde;o perceber que a vacina apenas contra uma (e n&atilde;o &ldquo;todas as doen&ccedil;as&rdquo;) &eacute; pr&eacute;-requisito para a viagem. O trecho &ldquo;en Espa&ntilde;a los animales deben haber sido vacinados contra la <strong>rabia</strong>&rdquo; traz essa informa&ccedil;&atilde;o e mostra que a &uacute;nica doen&ccedil;a com a qual os donos devem se preocupar &eacute; a raiva.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12748,2654,'E','<p>vacinar o animal e depois solicitar o passaporte dele.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>&Eacute; preciso estar atento ao termo &ldquo;antes&rdquo;, o qual expressa que o animal deve ser vacinado contra a raiva para que, em seguida, seu dono solicite seu passaporte. Isso se encontra no trecho &ldquo;en Espa&ntilde;a los animales deben haber sido vacinados contra la rabia <strong>antes </strong>de su due&ntilde;o solicitar la documentaci&oacute;n&rdquo;.</p>\r\n',1);
