INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12922,2695,'A','<p>&ldquo;A Academia (Brasileira de Letras) encara essa aprova&ccedil;&atilde;o como um marco hist&oacute;rico. Inscreve-se, finalmente, a L&iacute;ngua Portuguesa no rol daquelas que conseguiram beneficiar-se h&aacute; mais tempo da unifica&ccedil;&atilde;o de seu sistema de grafar, numa demonstra&ccedil;&atilde;o de consci&ecirc;ncia da pol&iacute;tica do idioma e de maturidade na defesa, difus&atilde;o e ilustra&ccedil;&atilde;o da l&Iacute;ngua da Lusofonia.&rdquo;</p>\r\n\r\n<p><br />\r\n<sub>SANDRONI, C. Presidente da ABL. Dispon&iacute;vel em: http://academia.org.br. Acesso em: 10 nov. 2008.</sub></p>\r\n','',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12923,2695,'B','<p>&ldquo;Acordo ortogr&aacute;fico? N&atilde;o, obrigado. Sou contra. Visceralmente contra. Filosoficamente contra. Linguisticamente contra. Eu gosto do &ldquo;c&rdquo; do &ldquo;actor&rdquo; e o &ldquo;p&rdquo; de &ldquo;cepticismo&rdquo;. Representam um patrim&ocirc;nio, uma pegada etimol&oacute;gica que faz parte de uma identidade cultural. A pluralidade &eacute; um valor que deve ser estudado e respeitado. Aceitar essa aberra&ccedil;&atilde;o significa apenas que a irmandade entre Portugal e o Brasil continua a ser a irmandade do atraso.&rdquo;</p>\r\n\r\n<p><br />\r\n<sub>COUTINHO, J. P. <strong>Folha de S&atilde;o Paulo, Ilustrada</strong>. 28 set. 2008, E1 (adaptado).</sub></p>\r\n','',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12924,2695,'C','<p>&ldquo;H&aacute; um conjunto de necessidades pol&iacute;ticas e econ&ocirc;micas com vista &agrave; internacionaliza&ccedil;&atilde;o do portugu&ecirc;s como identidade e marca econ&ocirc;mica. E poss&iacute;vel que o (Femando) Pessoa, como produto de exporta&ccedil;&atilde;o, valha mais do que a PT (Portugal Telecom). Tem um valor econ&ocirc;mico &uacute;nico.&rdquo;</p>\r\n\r\n<p><br />\r\n<sub>RIBEIRO, J. A. P. Ministro da Cultura de Portugal. Dispon&iacute;vel em:&nbsp;http://ultimahora.publico.clix.pt. Acesso em: 10 nov. 2008.</sub></p>\r\n','',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12925,2695,'D','<p>&ldquo;&Eacute; um acto c&iacute;vico batermo-nos contra o Acordo Ortogr&aacute;fico.&rdquo; &ldquo;O acordo n&atilde;o leva a unidade nenhuma.&rdquo; &ldquo;N&atilde;o se pode aplicar na ordem interna um instrumento que n&atilde;o est&aacute; aceito internacionalmente&rdquo; e nem assegura &ldquo;a defesa da l&iacute;ngua como patrim&oacute;nio, como prev&ecirc; a Constitui&ccedil;&atilde;o nos artigos 9&deg; e 68&deg;.&rdquo;</p>\r\n\r\n<p><br />\r\n<sub>MOURA, V. G. Escritor e eurodeputado. Dispon&iacute;vel em: www.mundoportugues.org. Acesso em: 10 nov. 2008.</sub></p>\r\n','',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12926,2695,'E','<p>&ldquo;Se &eacute; para ter uma lusofonia, o conceito [unifica&ccedil;&atilde;o da l&iacute;ngua] deve ser mais abrangente e temos de estar em paridade. Unidade n&atilde;o significa que temos que andar todos ao mesmo passo. N&atilde;o &eacute; necess&aacute;rio que nos tornemos homog&eacute;neos. At&eacute; porque o que enriquece a l&iacute;ngua portuguesa s&atilde;o as diversas literaturas e formas de utiliza&ccedil;&atilde;o.&rdquo;</p>\r\n\r\n<p><br />\r\n<sub>RODRIGUES, M. H. Presidente do Instituto Portugu&ecirc;s do Oriente, sediado em Macau. Dispon&iacute;vel em: http://taichungpou.blogspot.com. Acesso em: 10. nov. 2008 (adaptado).</sub></p>\r\n','',0);
