INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13812,2891,'A','<p>uma den&uacute;ncia de quadrilhas que se organizam em torno do narcotr&aacute;fico.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O editorial apresenta sim uma den&uacute;ncia da atividade das quadrilhas, mas isso &eacute; informar e n&atilde;o argumentar. Essas informa&ccedil;&otilde;es s&atilde;o utilizadas como base para o argumento de que a repress&atilde;o ao crime deve aumentar. O aluno optar&aacute; por esse distrator caso n&atilde;o haja percep&ccedil;&atilde;o da caracter&iacute;stica basilar da den&uacute;ncia.</p>\r\n',0);
