INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13870,2904,'A','<p><strong>ap&oacute;s</strong> &eacute; conectivo de causa, j&aacute; que apresenta o motivo de a zaga alvinegra ter rebatido a bola de cabe&ccedil;a.</p>\r\n','<p>INCORRETA<br />\r\n&ldquo;Ap&oacute;s&rdquo; &eacute; um conectivo temporal, entrega a ideia de posterioridade. O aluno poder&aacute; marcar essa alternativa, pois a descri&ccedil;&atilde;o da jogada come&ccedil;a no momento em que o &ldquo;ap&oacute;s&rdquo; aparece.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13871,2904,'B','<p><strong>enquanto</strong>&nbsp; tem um significado alternativo, porque conecta duas op&ccedil;&otilde;es poss&iacute;veis para serem aplicadas no jogo.</p>\r\n','<p>INCORRETA<br />\r\n&ldquo;Enquanto&rdquo; tamb&eacute;m tem teor temporal, dita que as a&ccedil;&otilde;es est&atilde;o acontecendo simultaneamente. O aluno pode vir a escolher esse distrator, pois as a&ccedil;&otilde;es s&atilde;o distintas.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13872,2904,'C','<p><strong>no entanto</strong> tem significado de tempo, porque ordena os fatos observados no jogo em ordem cronol&oacute;gica de ocorr&ecirc;ncia.</p>\r\n','<p>INCORRETA<br />\r\n&ldquo;No entanto&rdquo; apresenta uma adversidade, ou seja, algo que tende a ter um resultado e tem outro. O aluno s&oacute; escolher&aacute; esse distrator se considerar apenas que a ordem cronol&oacute;gica &eacute; respeitada no texto.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13873,2904,'D','<p><strong>mesmo&nbsp;</strong>traz ideia de concess&atilde;o, j&aacute; que em&nbsp;&ldquo;com mais posse de bola&rdquo;, ter dificuldade n&atilde;o &eacute; algo naturalmente esperado.</p>\r\n','<p>CORRETA<br />\r\n&ldquo;Mesmo&rdquo; desempenha a fun&ccedil;&atilde;o de concess&atilde;o, pois, apesar da vantagem na posse de bola, o time de Cuca foi incapaz de acertar o gol, o que caracteriza uma quebra de expectativa.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13874,2904,'E','<p><strong>por causa de</strong> indica consequ&ecirc;ncia, porque as tentativas de ataque do Flamengo motivaram o Botafogo a fazer um bloqueio.</p>\r\n','<p>INCORRETA<br />\r\n&ldquo;Por causa de&rdquo; indica causa, motiva&ccedil;&atilde;o, logo n&atilde;o pode ser consequ&ecirc;ncia, pois &eacute; a raiz do resultado e n&atilde;o ele. O aluno pode acessar esse distrator caso confunda o papel da consequ&ecirc;ncia na rela&ccedil;&atilde;o causa X consequ&ecirc;ncia.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13903,2912,'A','<p>os homens est&atilde;o matriculados em menor propor&ccedil;&atilde;o em cursos de Matem&aacute;tica que em Medicina por lidarem melhor com pessoas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que marca esta alternativa tem, provavelmente, dificuldade em interpretar gr&aacute;ficos, uma vez que as informa&ccedil;&otilde;es dessa alternativa contrariam as apresentadas na imagem. O texto explicativo ao lado direito do gr&aacute;fico destaca as maiores habilidades dos homens entre &quot;lidar com coisas inanimadas e abstra&ccedil;&otilde;es&quot;, e n&atilde;o os coloca, portanto, como mais h&aacute;beis em &quot;lidar com pessoas&quot; do que as mulheres. Al&eacute;m disso, &eacute; poss&iacute;vel perceber que o curso de Medicina &eacute; mais frequentado por mulheres do que por homens, observando as barras verticais.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13904,2912,'B','<p>as mulheres est&atilde;o matriculadas em maior percentual em cursos que exigem capacidade de compreens&atilde;o dos seres humanos.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>A breve explica&ccedil;&atilde;o ao lado esquerdo do gr&aacute;fico explicita que as mulheres &quot;t&ecirc;m mais habilidade para compreender pessoas&quot;. Al&eacute;m disso, as barras localizadas &agrave; esquerda do eixo vertical do gr&aacute;fico representam as &aacute;reas dos cursos mais frequentados por mulheres. Esses cursos, dentre os apresentados, s&atilde;o justamente os que mais dependem da capacidade do profissional em compreender os seres humanos (Psicologia, Humanas-artes, Educa&ccedil;&atilde;o e Medicina).</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13905,2912,'C','<p>as mulheres est&atilde;o matriculadas em percentual maior em F&iacute;sica que em Minera&ccedil;&atilde;o por tenderem a trabalhar melhor com abstra&ccedil;&otilde;es.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno marca a alternativa, provavelmente, por mesclar as informa&ccedil;&otilde;es sobre homens e mulheres. Entretanto, o texto explicativo ao lado direito do gr&aacute;fico deixa claro que s&atilde;o os homens os melhores em lidar com abstra&ccedil;&otilde;es. Ao mesmo tempo, Verifica-se pelas barras do gr&aacute;fico, &agrave; direita do eixo vertical, que os cursos de F&iacute;sica e Minera&ccedil;&atilde;o s&atilde;o mais frequentados por homens.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13906,2912,'D','<p>os homens e as mulheres est&atilde;o matriculados na mesma propor&ccedil;&atilde;o em cursos que exigem habilidades semelhantes na mesma &aacute;rea.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que marca esta alternativa tem, provavelmente, dificuldade em interpretar gr&aacute;ficos, uma vez que ignora as diferen&ccedil;as apresentadas. Os textos explicativos sobre cada g&ecirc;nero, apresentados &agrave; esquerda (mulheres) e &agrave; direita (homens) do gr&aacute;fico, deixam claro que as habilidades de cada um s&atilde;o diferentes. N&atilde;o se pode dizer, para nenhum ds cursos, que homens e mulheres est&atilde;o matriculados na mesma propor&ccedil;&atilde;o em cursos que exigem habilidades semelhantes na mesma &aacute;rea.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (13907,2912,'E','<p>as mulheres est&atilde;o matriculadas em menor n&uacute;mero em Psicologia por sua habilidade de lidarem melhor com coisas que com sujeitos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O estudante marca a alternativa por mesclar as informa&ccedil;&otilde;es sobre homens e mulheres. Entretanto, pode-se observar pelas barras localizadas &agrave; esquerda do eixo vertical que as mulheres est&atilde;o matriculadas em maior n&uacute;mero em Psicologia do que os homens. O texto explicativo &agrave; esquerda do gr&aacute;fico tamb&eacute;m explicita que as mulheres lidam melhor com pessoas do que com coisas inanimadas.</p>\r\n',0);
