INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1459,294,'B','<p>maior do que o valor da primeira encomenda, mas n&atilde;o o dobro.</p>\r\n','<p>CORRETA.</p>\r\n\r\n<p>Para a 1&ordf; encomenda:</p>\r\n\r\n<p>Cada quadro cont&eacute;m:</p>\r\n\r\n<p>0,25.0,5 = 0,125 m&sup2;</p>\r\n\r\n<p>0,25.2 + 0,5.2 = 1,5 m de moldura</p>\r\n\r\n<p>Como s&atilde;o 8 quadros, temos que a 1&ordf; encomenda ser&aacute; de 0,125.8.20 + 1,5.8.15 +10 = 210 reais.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Para a 2&ordf; encomenda:</p>\r\n\r\n<p>Cada quadro cont&eacute;m:</p>\r\n\r\n<p>0,5.1,0 = 0,5 m&sup2;</p>\r\n\r\n<p>0,5.2 + 1.2 = 3 m de moldura</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Como s&atilde;o 8 quadros, temos que a 2&ordf; encomenda ser&aacute; de 0,5.8.20 + 3.8.15 +10 = 450 reais.</p>\r\n\r\n<p>Logo, o valor da segunda encomenda ser&aacute;&nbsp;maior do que o valor da primeira encomenda, mas n&atilde;o o dobro.</p>\r\n',1);
