INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (37395,7796,'A','<p>igualit&aacute;ria, com um n&iacute;vel satisfat&oacute;rio de distribui&ccedil;&atilde;o&nbsp;da renda.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A desigualdade social, fruto da grande concentra&ccedil;&atilde;o de renda, garantia aos coron&eacute;is o poder sobre a grande parte da popula&ccedil;&atilde;o, que n&atilde;o era assistida por ningu&eacute;m a n&atilde;o ser por seus patr&otilde;es, e que n&atilde;o tinha direitos assegurados pelo Estado.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (37396,7796,'B','<p>estagnada, com uma relativa harmonia entre as&nbsp;classes.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o &eacute; poss&iacute;vel atribuir &agrave; sociedade brasileira da Primeira Rep&uacute;blica uma &ldquo;harmonia entre as classes&rdquo;. Esse per&iacute;odo, marcado pela concentra&ccedil;&atilde;o de poder na m&atilde;o dos grandes latifundi&aacute;rios, tamb&eacute;m apresentou diversos epis&oacute;dios de resist&ecirc;ncia da popula&ccedil;&atilde;o oprimida por esse cen&aacute;rio, e diversas rebeli&otilde;es e revoltas fizeram parte desse per&iacute;odo da hist&oacute;ria brasileira.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (37397,7796,'C','<p>tradicional, com a manuten&ccedil;&atilde;o da escravid&atilde;o nos&nbsp;engenhos como forma produtiva t&iacute;pica.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Logo antes do in&iacute;cio da Primeira Rep&uacute;blica (1889-1930), em 1888, a escravid&atilde;o foi abolida no Brasil. Apesar de serem mantidos resqu&iacute;cios dessa pr&aacute;tica, seja na recorrente manuten&ccedil;&atilde;o da escravid&atilde;o clandestina, seja na explora&ccedil;&atilde;o sistem&aacute;tica e tratamento discriminat&oacute;rio contra a popula&ccedil;&atilde;o negra, n&atilde;o &eacute; poss&iacute;vel enxergar a &ldquo;escravid&atilde;o nos engenhos como forma produtiva t&iacute;pica&rdquo;, j&aacute; que ela se colocava contra a lei.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (37398,7796,'D','<p>ditatorial, perturbada por um constante clima de&nbsp;opress&atilde;o mantido pelo ex&eacute;rcito e pol&iacute;cia.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O clima de opress&atilde;o mantido contra os trabalhadores rurais era garantido pelos coron&eacute;is e suas pequenas &ldquo;mil&iacute;cias&rdquo;, extraoficiais, contra as quais o ex&eacute;rcito e a pol&iacute;cia n&atilde;o voltavam sua aten&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (37399,7796,'E','<p>agr&aacute;ria, marcada pela concentra&ccedil;&atilde;o da terra e do&nbsp;poder pol&iacute;tico local e regional.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Os coron&eacute;is, figuras t&iacute;picas da Primeira Rep&uacute;blica, eram os grandes fazendeiros, favorecidos pela distribui&ccedil;&atilde;o fundi&aacute;ria brasileira extremamente concentrada na m&atilde;o de poucas pessoas. A grande parte da popula&ccedil;&atilde;o, residente no meio rural, era comandada por esses coron&eacute;is, que se valiam de posi&ccedil;&otilde;es paternalistas ou extremamente violentas para direcionar o voto de seus trabalhadores. Assim, a concentra&ccedil;&atilde;o de poder estava diretamente relacionada &agrave; concentra&ccedil;&atilde;o de terra, que era mesmo a origem da manuten&ccedil;&atilde;o do coronelismo.</p>\r\n',1);
