INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (40684,8461,'A','<p>Benzeno.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa n&atilde;o compreende a rela&ccedil;&atilde;o proposta no enunciado e/ou erra a estequiometria das rea&ccedil;&otilde;es termoqu&iacute;micas.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (40685,8461,'B','<p>Metano.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa n&atilde;o compreende o enunciado e divide a quantidade de energia liberada por mol de combust&iacute;vel pela quantidade em mol de di&oacute;xido de carbono.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (40686,8461,'C','<p>Glicose.</p>\r\n','<p>CORRETA.<br />\r\nConsiderando-se um mesmo valor de energia para todos os combust&iacute;veis apresentados, &eacute; poss&iacute;vel relacionar sua combust&atilde;o &agrave; quantidade, em mols, de di&oacute;xido de carbono liberado. Assim, considerando-se o maior valor apresentado na tabela, - 5 471 kJ/mol, tem-se:</p>\r\n\r\n<p>Benzeno<br />\r\n1mol C$_{6}$H$_{6}$&nbsp;&ndash; 6 mol CO$_{2}$&nbsp;&ndash; 3 268 kJ<br />\r\n6mol CO$_{2}$&nbsp;&ndash; 3 268<br />\r\nx &ndash; 5 471<br />\r\nx &asymp; 10 mol CO$_{2}$</p>\r\n\r\n<p>Etanol<br />\r\n1mol C$_{2}$H$_{5}$OH &ndash; 2 mol CO$_{2}$&nbsp;&ndash; 1 368 kJ<br />\r\n2mol CO$_{2}$ &ndash; 1 368<br />\r\ny &ndash; 5 471<br />\r\ny &asymp; 3,9 mol CO$_{2}$</p>\r\n\r\n<p>Glicose<br />\r\n1mol C$_{6}$H$_{12}$O$_{6}$&nbsp;&ndash; 6 mol CO$_{2}$&nbsp;&ndash; 2 808 kJ<br />\r\n6mol CO$_{2}$&nbsp;&ndash; 2 808<br />\r\nz &ndash; 5 471<br />\r\nz &asymp; 11,7 mol CO$_{2}$</p>\r\n\r\n<p>Metano<br />\r\n1mol CH$_{4}$&nbsp;&ndash; 1 mol CO$_{2}$&nbsp;&ndash; 890 kJ<br />\r\n1mol CO$_{2}$&nbsp;&ndash; 890<br />\r\nw &ndash; 5 471<br />\r\nw &asymp; 6,1 mol CO$_{2}$</p>\r\n\r\n<p>Octano<br />\r\n1mol C$_{8}$H$_{18}$&nbsp;&ndash; 8 mol CO$_{2}$&nbsp;&ndash; 5 471 kJ</p>\r\n\r\n<p>Portanto, comparando-se todas as subst&acirc;ncias apresentadas, a glicose representa o combust&iacute;vel que libera a maior quantidade de di&oacute;xido de carbono, considerando-se a mesma energia envolvida em todas as combust&otilde;es.</p>\r\n',1);
