INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (162,23,'C','<p>apresenta uma magnetiza&ccedil;&atilde;o desprez&iacute;vel sob a a&ccedil;&atilde;o do &iacute;m&atilde; permanente.</p>\r\n','<p>CORRETA.<br />\r\nO funcionamento do amplificador &eacute; devido &agrave; magnetiza&ccedil;&atilde;o das cordas da guitarra. Para que elas sejam magnetizadas, devem ser feitas com um material paramagn&eacute;tico. Nesse caso, as cordas de n&aacute;ilon n&atilde;o ser&atilde;o magnetizadas quando sujeitas ao campo magn&eacute;tico externo.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (163,23,'D','<p>induz correntes el&eacute;tricas na bobina mais intensas que a capacidade do captador.</p>\r\n','<p>INCORRETA.<br />\r\nO aluno que seleciona essa alternativa tem o pensamento oposto ao correto: acredita que as cordas de n&aacute;ilon ter&atilde;o efeito amplificado.</p>\r\n',0);
