INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (8141,1669,'A','<p>introdu&ccedil;&atilde;o de um parasita no ambiente da esp&eacute;cie que se deseja combater.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Parasitas s&atilde;o seres vivos que se alojam interna ou externamente em outros organismos e retiram recursos indispens&aacute;veis &agrave; sobreviv&ecirc;ncia dos mesmos. Os embri&otilde;es da microvespa Trichogramma sp. usufruem dos recursos necess&aacute;rios para o desenvolvimento das larvas das borboletas, o que caracteriza uma rela&ccedil;&atilde;o de parasitismo.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (8142,1669,'B','<p>introdu&ccedil;&atilde;o de um gene letal nas borboletas, a fim de diminuir o n&uacute;mero de indiv&iacute;duos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que seleciona essa alternativa n&atilde;o compreende que a microvespa Trichogramma sp. introduz embri&otilde;es nos ovos das borboletas, e n&atilde;o um gene letal.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (8143,1669,'C','<p>competi&ccedil;&atilde;o entre a borboleta e a microvespa para a obten&ccedil;&atilde;o de recursos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que seleciona essa alternativa n&atilde;o compreende que n&atilde;o h&aacute; competi&ccedil;&atilde;o por recursos entre borboleta e microvespa, pois a microvespa se aloja internamente nos ovos das borboletas e retira recursos indispens&aacute;veis ao desenvolvimento desses insetos, o que caracteriza uma rela&ccedil;&atilde;o de parasitismo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (8144,1669,'D','<p>modifica&ccedil;&atilde;o do ambiente para selecionar indiv&iacute;duos mais bem adaptados.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que seleciona essa alternativa n&atilde;o compreende que n&atilde;o ocorre nenhuma modifica&ccedil;&atilde;o no ambiente na t&eacute;cnica de controle biol&oacute;gico.</p>\r\n',0);
