INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1811,368,'A','<p>discutir sobre o conte&uacute;do do seu trabalho j&aacute; entregue.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>N&atilde;o h&aacute; discuss&atilde;o, uma vez que a personagem n&atilde;o tenta problematizar o conte&uacute;do do trabalho, como descrito pelo distrator, mas sim convencer a professora de que o trabalho foi feito, mas de uma maneira incomum. Pelo fato de a charge conter os temas do trabalho, esse distrator pode ser selecionado pelo aluno.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1812,368,'B','<p>elogiar o tema proposto para o relat&oacute;rio solicitado.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O elogio costuma ser constitu&iacute;do por adjetiva&ccedil;&atilde;o positiva, mas isso n&atilde;o acontece na charge. Os temas do trabalho s&atilde;o apenas citados como constituintes do trabalho &ldquo;feito&rdquo; pela personagem. Como o tema do trabalho da personagem na charge &eacute; sobre a import&acirc;ncia de alguns temas, o aluno pode entender isso como um elogio e selecionar este distrator.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1813,368,'C','<p>sugerir temas para novas pesquisas e relat&oacute;rios.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A personagem n&atilde;o sugere novos temas para o trabalho. Ela cita os temas sobre os quais o seu trabalho discorre. O aluno s&oacute; selecionar&aacute; este distrator caso n&atilde;o compreenda o texto verbal da charge.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1814,368,'D','<p>reclamar do curto prazo para entrega do trabalho.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Para haver uma reclama&ccedil;&atilde;o, deve haver uma situa&ccedil;&atilde;o de discord&acirc;ncia, mas isso n&atilde;o ocorre na charge. A personagem n&atilde;o discorda do trabalho, ela apenas n&atilde;o o fez e est&aacute; tentando ludibriar a professora. O aluno pode escolher esse distrator caso entenda como protesto a n&atilde;o confec&ccedil;&atilde;o do trabalho.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1815,368,'E','<p>convencer de que fez o relat&oacute;rio solicitado.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Em um &uacute;ltimo esfor&ccedil;o para entregar o trabalho, a personagem mais nova apela para o absurdo de reafirmar que o fez, mas que a entrega ser&aacute; via telepatia. Ela usa de ironia para conectar o tema de seu trabalho (economia de papel, eletricidade e outros recursos) com o fato de n&atilde;o entregar o trabalho no papel para sua professora.</p>\r\n',1);
