INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12357,2568,'E','<p>atribui aos brasileiros uma habilidade maior com instrumentos musicais.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O trecho apresenta os brasileiros tocando cavaquinho e viol&atilde;o, mas n&atilde;o descreve as habilidades dos portugueses com instrumentos, n&atilde;o sendo poss&iacute;vel, portanto, estabelecer par&acirc;metros de compara&ccedil;&atilde;o com base nos elementos do texto.</p>\r\n',0);
