INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12373,2572,'A','<p>informar os consumidores em geral sobre a atua&ccedil;&atilde;o do Conar.</p>\r\n','<p>CORRETA<br />\r\nO texto apresenta informa&ccedil;&otilde;es sobre a atua&ccedil;&atilde;o do Conar e tem como p&uacute;blico-alvo os consumidores. Isso &eacute; comprovado tanto pelo &uacute;ltimo par&aacute;grafo, em que h&aacute; um alerta para que o consumidor denuncie propaganda enganosa, quanto pelo ve&iacute;culo que o divulgou, a revista Veja.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12374,2572,'B','<p>conscientizar publicit&aacute;rios do compromisso &eacute;tico ao elaborar suas pe&ccedil;as publicit&aacute;rias.</p>\r\n','<p>INCORRETA<br />\r\nApesar de o texto envolver informa&ccedil;&otilde;es sobre a atua&ccedil;&atilde;o do Conar em assegurar a veracidade da publicidade e refor&ccedil;ar o compromisso &eacute;tico necess&aacute;rio ao elabor&aacute;-la, o objetivo do texto n&atilde;o &eacute; somente esse, al&eacute;m disso, o p&uacute;blico-alvo s&atilde;o os consumidores, n&atilde;o os publicit&aacute;rios.&nbsp;</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12375,2572,'C','<p>alertar chefes de fam&iacute;lia, para que eles fiscalizem o conte&uacute;do das propagandas veiculadas pela m&iacute;dia.</p>\r\n','<p>INCORRETA<br />\r\nApesar de os chefes de fam&iacute;lia estarem contidos no grupo que constitui o p&uacute;blico-alvo do texto, este n&atilde;o se restringe a eles. O respondente poder&aacute; interpretar erroneamente que o alerta ao consumidor para denunciar propagandas enganosas se dirige somente aos chefes de fam&iacute;lia, entretanto, &eacute; direcionado a qualquer consumidor.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12376,2572,'D','<p>chamar a aten&ccedil;&atilde;o de empres&aacute;rios e anunciantes em geral para suas responsabilidades ao contratarem publicit&aacute;rios sem &eacute;tica.</p>\r\n','<p>INCORRETA<br />\r\nAo interpretar incorretamente as estrat&eacute;gias argumentativas do texto que estabelecem como p&uacute;blico-alvo o consumidor, o aluno poder&aacute; julgar erroneamente que os empres&aacute;rios e anunciantes s&atilde;o o p&uacute;blico-alvo. Al&eacute;m disso, os alertas do texto n&atilde;o envolvem o ambiente profissional publicit&aacute;rio, portanto, n&atilde;o poderiam ser direcionados a ele.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12377,2572,'E','<p>chamar a aten&ccedil;&atilde;o de empresas para os efeitos nocivos que elas podem causar &agrave; sociedade, se compactuarem com propagandas enganosas.</p>\r\n','<p>INCORRETA<br />\r\nO respondente desatento, caso se atenha somente &agrave; informa&ccedil;&atilde;o sobre a atua&ccedil;&atilde;o do Conar, que age na autorregulamenta&ccedil;&atilde;o das publicidades e tem a autoridade de aplicar puni&ccedil;&otilde;es &agrave;s institui&ccedil;&otilde;es, poder&aacute; considerar erroneamente que o objetivo do texto seria alertar a empresa do ramo publicit&aacute;rio. Entretanto, ao interpretar as estrat&eacute;gias argumentativas e o suporte em que o texto &eacute; veiculado, n&atilde;o ser&aacute; poss&iacute;vel manter tal afirma&ccedil;&atilde;o.</p>\r\n',0);
