INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12394,2576,'B','<p>enfatizar a tend&ecirc;ncia da sociedade contempor&acirc;nea de buscar h&aacute;bitos alimentares saud&aacute;veis, refor&ccedil;ando tal postura.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O estudante pode ser guiado por seu repert&oacute;rio pessoal, reconhecendo que a sociedade atual busca, de fato, h&aacute;bitos saud&aacute;veis. Essa, por&eacute;m, n&atilde;o &eacute; a finalidade do texto. O ado&ccedil;ante sequer &eacute; considerado uma escolha saud&aacute;vel, de acordo com certos m&eacute;dicos e nutricionistas.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12395,2576,'C','<p>criticar o consumo excessivo de produtos industrializados por parte da popula&ccedil;&atilde;o, propondo a redu&ccedil;&atilde;o desse consumo.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O respondente pode ser guiado por seu repert&oacute;rio pessoal, reconhecendo a cr&iacute;tica ao consumo de produtos industrializados, na sociedade. Esse, contudo, n&atilde;o &eacute; o objetivo do texto.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12396,2576,'D','<p>associar o voc&aacute;bulo &ldquo;a&ccedil;&uacute;car&rdquo; &agrave; imagem do corpo fora de forma, sugerindo a substitui&ccedil;&atilde;o desse produto pelo ado&ccedil;ante.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>&Eacute; importante que o aluno esteja atento &agrave; linguagem n&atilde;o verbal, que apresenta um saco de a&ccedil;&uacute;car imitando um ventre avantajado (com detalhe para o suposto umbigo). A linguagem verbal, em &ldquo;Mude sua embalagem&rdquo;, dirige-se diretamente ao interlocutor, que &eacute; convidado a se identificar com a imagem e a pensar no ado&ccedil;ante como uma alternativa para o emagrecimento.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12397,2576,'E','<p>relacionar a imagem do saco de a&ccedil;&uacute;car a um corpo humano que n&atilde;o desenvolve atividades f&iacute;sicas, incentivando a pr&aacute;tica esportiva.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A primeira parte da afirmativa, relativa &agrave; associa&ccedil;&atilde;o do corpo humano com o saco de a&ccedil;&uacute;car, procede, confundindo o leitor que n&atilde;o esteja atento &agrave; segunda parte da frase.</p>\r\n',0);
