INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12445,2586,'C','<p>a preserva&ccedil;&atilde;o da identidade, demonstrada pela conserva&ccedil;&atilde;o do idioma, mesmo com a utiliza&ccedil;&atilde;o de novas tecnologias caracter&iacute;sticas da cultura de outros grupos sociais.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O respondente deve perceber que a preserva&ccedil;&atilde;o da l&iacute;ngua &eacute; tamb&eacute;m a preserva&ccedil;&atilde;o da identidade de um povo. &Eacute; o que faz a tribo Sapuca&iacute; ao propor uma nomenclatura particular a artefatos relacionados &agrave;s novas tecnologias.</p>\r\n',1);
