INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12449,2587,'B','<p>falantes que dominam a variedade padr&atilde;o do portugu&ecirc;s do Brasil demonstram usos que confirmam a diferen&ccedil;a entre a norma idealizada e a efetivamente praticada, mesmo por falantes mais escolarizados.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O estudante deve estar atento para o primeiro par&aacute;grafo, em que se afirma que at&eacute; mesmo falantes que dominam a norma padr&atilde;o realizam o mencionado uso da l&iacute;ngua.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12450,2587,'C','<p>moradores de diversas regi&otilde;es do pa&iacute;s que enfrentam dificuldades ao se expressarem na escrita revelam a constante modifica&ccedil;&atilde;o das regras de emprego de pronomes e os casos especiais de concord&acirc;ncia.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A men&ccedil;&atilde;o &agrave; concord&acirc;ncia e ao uso dos pronomes, que acontece no segundo par&aacute;grafo do texto, pode atrair o olhar do aluno.</p>\r\n',0);
