INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12473,2592,'A','<p>a evolu&ccedil;&atilde;o ocorreu e com ela evolu&iacute;ram as dores de cabe&ccedil;a, o estresse e a falta de aten&ccedil;&atilde;o &agrave; fam&iacute;lia.</p>\r\n','<p>INCORRETA<br />\r\nO aluno pode marcar a op&ccedil;&atilde;o apoiado em seu repert&oacute;rio pessoal, pois &eacute; comum, na m&iacute;dia, por exemplo, que se relacione a evolu&ccedil;&atilde;o tecnol&oacute;gica ao estresse e &agrave; falta de aten&ccedil;&atilde;o &agrave; fam&iacute;lia. Todavia, a fam&iacute;lia, por exemplo, nem &eacute; identific&aacute;vel no cartum.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12474,2592,'B','<p>a vida sem o computador tornou-se quase invi&aacute;vel, mas se tem diminu&iacute;do problemas de vis&atilde;o cansada.</p>\r\n','<p>INCORRETA<br />\r\nA primeira parte da alternativa &eacute; plaus&iacute;vel, o que pode atrair o respondente. Ele tamb&eacute;m pode ser levado pelo olhar fixo da figura humana, que se aproxima do computador na imagem. Entretanto, n&atilde;o h&aacute; nada que relacione o uso desse recurso tecnol&oacute;gico &agrave; diminui&ccedil;&atilde;o dos problemas de vista cansada. Pelo contr&aacute;rio, a exposi&ccedil;&atilde;o durante v&aacute;rias horas a dispositivos digitais tem contribu&iacute;do para que ele aumente entre a popula&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12475,2592,'C','<p>a utiliza&ccedil;&atilde;o demasiada do computador tem proporcionado o surgimento de cientistas que apresentam les&atilde;o por esfor&ccedil;o repetitivo.</p>\r\n','<p>INCORRETA<br />\r\nO estudante pode j&aacute; ter ouvido falar sobre a associa&ccedil;&atilde;o entre o uso de computadores e a les&atilde;o por esfor&ccedil;o repetitivo. Esse fato, bem como a rela&ccedil;&atilde;o entre ele e o surgimento de cientistas, por&eacute;m, n&atilde;o podem ser verificados pela imagem.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12476,2592,'D','<p>o homem criou o computador, que evoluiu, e hoje opera v&aacute;rias a&ccedil;&otilde;es antes feitas pelas pessoas, tornando-as sedent&aacute;rias ou obesas.</p>\r\n','<p>INCORRETA<br />\r\nA rela&ccedil;&atilde;o entre obesidade, sedentarismo e uso de computadores &eacute; comum na m&iacute;dia e no senso comum, o que pode ludibriar o estudante. Entretanto, essa alternativa n&atilde;o est&aacute; correta uma vez que faz uma generaliza&ccedil;&atilde;o falsa. Ela afirma que todas as pessoas que usam computadores tornam-se obesas ou sedent&aacute;rias, apesar de n&atilde;o ser poss&iacute;vel dizer que isso &eacute; v&aacute;lido para todas as pessoas, sem exce&ccedil;&otilde;es.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (12477,2592,'E','<p>o uso cont&iacute;nuo do computador de forma inadequada tem ocasionado m&aacute; postura corporal.</p>\r\n','<p>CORRETA<br />\r\nDeve-se estar atento a duas express&otilde;es do enunciado: &ldquo;problema f&iacute;sico&rdquo; e &ldquo;qualidade de vida&rdquo;. A partir delas, observa-se, na imagem, a postura progressivamente mais encurvada do indiv&iacute;duo, comprovando a m&aacute; postura corporal.</p>\r\n',1);
