INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (1227,247,'E','<p>89.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>De acordo com o enunciado os &uacute;nicos algarismos que aparecer&atilde;o ser&aacute; 1,3,5,7 e 9.</p>\r\n\r\n<p>Ent&atilde;o teremos:</p>\r\n\r\n<p>N&uacute;meros iniciados em 3,5 ou 1: $\\rightarrow$ 3&times;4&times;3&times;2&times;1 = 72</p>\r\n\r\n<p>N&uacute;meros iniciados em 71 ou 73: $\\rightarrow$ 1&times;2&times;3&times;2&times;1=12</p>\r\n\r\n<p>N&uacute;meros iniciados em 751 ou 753:$\\rightarrow$ 1&times;1&times;2&times;2&times;1=4</p>\r\n\r\n<p>Pela soma, sabemos que existem 72 + 12 + 4 = 88 n&uacute;meros anteriores. Logo, a posi&ccedil;&atilde;o do n&uacute;mero &eacute; a de 89&deg;.</p>\r\n',1);
