INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41240,8577,'A','<p>a humanidade do rei, pois retrata um homem comum,&nbsp;sem os adornos pr&oacute;prios &agrave; vestimenta real.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A imagem satiriza a constru&ccedil;&atilde;o da imagem do rei Lu&iacute;s XIV por meio da exposi&ccedil;&atilde;o separada da vestimenta real; do corpo do rei em si; e da sobreposi&ccedil;&atilde;o da vestimenta sobre o monarca. Assim, o foco da charge n&atilde;o &eacute; a humanidade do rei; mas a discrep&acirc;ncia entre a sua figura despida e a sua figura enquanto rei vestido como ditava a sua condi&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41241,8577,'B','<p>a unidade entre o p&uacute;blico e o privado, pois a figura do&nbsp;rei com a vestimenta real representa o p&uacute;blico e sem&nbsp;a vestimenta real, o privado.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A imagem satiriza a constru&ccedil;&atilde;o da imagem do rei Lu&iacute;s XIV por meio da exposi&ccedil;&atilde;o separada da vestimenta real; do corpo do rei em si; e da sobreposi&ccedil;&atilde;o da vestimenta sobre o monarca. Assim, o foco da &eacute; a discrep&acirc;ncia entre a sua figura despida e a sua figura enquanto rei vestido como ditava a sua condi&ccedil;&atilde;o. Essa condi&ccedil;&atilde;o, inclusive, fazia com que as esferas do p&uacute;blico e do privado se mesclassem.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41242,8577,'C','<p>o v&iacute;nculo entre monarquia e povo, pois leva&nbsp;ao conhecimento do p&uacute;blico a figura de um rei&nbsp;despretensioso e distante do poder pol&iacute;tico.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A imagem satiriza a constru&ccedil;&atilde;o da imagem do rei Lu&iacute;s XIV por meio da exposi&ccedil;&atilde;o separada da vestimenta real; do corpo do rei em si; e da sobreposi&ccedil;&atilde;o da vestimenta sobre o monarca. Essas etapas n&atilde;o eram publicizadas, tampouco eram levadas &ldquo;ao conhecimento do p&uacute;blico&rdquo; as reais dimens&otilde;es do rei. A imagem do rei era constru&iacute;da para impressionar e para que fosse vinculada a ideia de poder.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41243,8577,'D','<p>o gosto est&eacute;tico refinado do rei, pois evidencia a&nbsp;eleg&acirc;ncia dos trajes reais em rela&ccedil;&atilde;o aos de outros&nbsp;membros da corte.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A imagem satiriza a constru&ccedil;&atilde;o da imagem do rei Lu&iacute;s XIV por meio da exposi&ccedil;&atilde;o separada da vestimenta real; do corpo do rei em si; e da sobreposi&ccedil;&atilde;o da vestimenta sobre o monarca. &nbsp;N&atilde;o &eacute; poss&iacute;vel depreender da charge qualquer informa&ccedil;&atilde;o sobre a vestimenta tradicional de outros membros da corte.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41244,8577,'E','<p>a import&acirc;ncia da vestimenta para a constitui&ccedil;&atilde;o&nbsp;simb&oacute;lica do rei, pois o corpo pol&iacute;tico adornado&nbsp;esconde os defeitos do corpo pessoal.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>A imagem satiriza a constru&ccedil;&atilde;o da imagem do rei Lu&iacute;s XIV por meio da exposi&ccedil;&atilde;o separada da vestimenta real; do corpo do rei em si; e da sobreposi&ccedil;&atilde;o da vestimenta sobre o monarca. O resultado final traduz a imagem de um rei imponente, grandioso, investido de poder, e objetivava n&atilde;o revelar as fraquezas do corpo do monarca.</p>\r\n',1);
