INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41745,8678,'A','<p>constante viol&ecirc;ncia nos baba&ccedil;uais na conflu&ecirc;ncia&nbsp;de terras maranhenses, piauienses, paraenses e&nbsp;tocantinenses, regi&atilde;o com elevado &iacute;ndice de homic&iacute;dios.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A organiza&ccedil;&atilde;o do movimento das quebradeiras de coco-baba&ccedil;u se deve &agrave; luta pelo acesso &agrave; terra por essas mulheres, e n&atilde;o pela viol&ecirc;ncia nos baba&ccedil;uais. Al&eacute;m disso, a regi&atilde;o n&atilde;o se destaca pelo elevado &iacute;ndice de homic&iacute;dios. Dessa forma, observa-se duas lacunas nessa alternativa, que a tornam incorreta.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41746,8678,'B','<p>falta de identidade coletiva das trabalhadoras,&nbsp;migrantes das cidades e com pouco v&iacute;nculo hist&oacute;rico&nbsp;com as &aacute;reas rurais do interior do Tocantins, Par&aacute;,&nbsp;Maranh&atilde;o e Piau&iacute;.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A organiza&ccedil;&atilde;o do movimento das quebradeiras de coco-baba&ccedil;u, ao contr&aacute;rio do que essa alternativa defende, deve-se, justamente, &agrave; intera&ccedil;&atilde;o, identidade coletiva e forte v&iacute;nculo hist&oacute;rico que essas mulheres t&ecirc;m com essas &aacute;reas rurais. &Eacute; incorreto falar em falta de identidade coletiva das trabalhadoras, bem como &eacute; igualmente incorreto apont&aacute;-las como migrantes das cidades e com pouco v&iacute;nculo hist&oacute;rico com as &aacute;reas rurais. As quebradeiras de coco-baba&ccedil;u t&ecirc;m sua vida intimamente voltada ao universo da Mata de Cocais e seu engajamento permite sua organiza&ccedil;&atilde;o, estando a&iacute; a lacuna dessa alternativa, que a torna incorreta.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41747,8678,'C','<p>escassez de &aacute;gua nas regi&otilde;es de veredas, ambientes&nbsp;naturais dos baba&ccedil;us, causada pela constru&ccedil;&atilde;o&nbsp;de a&ccedil;udes particulares, impedindo o amplo acesso&nbsp;p&uacute;blico aos recursos h&iacute;dricos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>As veredas consistem em um tipo de forma&ccedil;&atilde;o vegetal do Cerrado que ocorre nas florestas-galeria. Caracterizadas pelos solos hidrom&oacute;rficos, apresentam topografia amena e &uacute;mida, mantendo parte da umidade em estratos de solo superficial e garantindo a umidade mesmo em per&iacute;odos de seca, tornando-se um ref&uacute;gio da fauna e flora, assim como local de abastecimento h&iacute;drico para os animais. Dessa forma, n&atilde;o podem ser apontadas como &aacute;reas de escassez de &aacute;gua, conforme o faz essa alternativa, estando a&iacute; a sua lacuna, que a torna incorreta.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41748,8678,'D','<p>progressiva devasta&ccedil;&atilde;o das matas dos cocais, em&nbsp;fun&ccedil;&atilde;o do avan&ccedil;o da sojicultura nos chapad&otilde;es do&nbsp;MeioNorte brasileiro.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A economia dos estados que ocupam a Mata dos Cocais se concentra na ind&uacute;stria de minera&ccedil;&atilde;o&mdash; com a extra&ccedil;&atilde;o de ouro, diamantes, n&iacute;quel, xisto betuminoso, bauxita e alum&iacute;nio&mdash;, na ind&uacute;stria qu&iacute;mica &mdash;manufaturas qu&iacute;micas, sendo as maiores ind&uacute;strias as de bauxita e caulim &mdash;,e na produ&ccedil;&atilde;o aliment&iacute;cia&mdash;&oacute;leos vegetais, pecu&aacute;ria e explora&ccedil;&atilde;o das palmeiras. Dessa forma, n&atilde;o &eacute; uma regi&atilde;o ocupada pela sojicultura, como defende essa alternativa, estando a&iacute; a sua lacuna, que a torna incorreta.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41749,8678,'E','<p>dificuldade imposta pelos fazendeiros e posseiros&nbsp;no acesso aos baba&ccedil;uais localizados no interior de&nbsp;suas propriedades.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O controle das terras pelos fazendeiros e posseiros na regi&atilde;o da Mata dos Cocais dificulta o acesso &agrave; terra pelas mulheres quebradeiras de coco-baba&ccedil;u, gerando uma organiza&ccedil;&atilde;o em prol das quebradeiras para manuten&ccedil;&atilde;o cultural e subsist&ecirc;ncia dessas mulheres. A sua atua&ccedil;&atilde;o contribui para mudan&ccedil;as nesse processo de disputa de poder, uma vez que amea&ccedil;a o poder dos fazendeiros e posseiros, corroborando a habilidade exigida para resolu&ccedil;&atilde;o da quest&atilde;o.</p>\r\n',1);
