INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41380,8605,'A','<p>a atividade dos comerciantes de a&ccedil;&uacute;car nos portos&nbsp;brasileiros.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Os comerciantes de a&ccedil;&uacute;car que desempenhavam suas atividades nos portos brasileiros n&atilde;o &ldquo;padeciam&rdquo; da mesma situa&ccedil;&atilde;o daquela simbolizada pela Paix&atilde;o de Cristo, representativa de um grande sofrimento. Dentro da cadeia produtiva do a&ccedil;&uacute;car, eles ocupavam lugar privilegiado.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41381,8605,'B','<p>a fun&ccedil;&atilde;o dos mestres de a&ccedil;&uacute;car durante a safra de cana.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Os mestres de a&ccedil;&uacute;car n&atilde;o &ldquo;padeciam&rdquo; da mesma situa&ccedil;&atilde;o daquela simbolizada pela Paix&atilde;o de Cristo, representativa de um grande sofrimento. Eles desempenhavam uma atividade remunerada e gozavam de um posto destacado dentro da hierarquia de poder e fun&ccedil;&otilde;es pr&oacute;pria do engenho, quando comparado com o contingente de negros escravizados nessas unidades de produ&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41382,8605,'C','<p>o sofrimento dos jesu&iacute;tas na convers&atilde;o dos amer&iacute;ndios.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Os jesu&iacute;tas empenhados na convers&atilde;o dos amer&iacute;ndios n&atilde;o &ldquo;padeciam&rdquo; da mesma situa&ccedil;&atilde;o daquela simbolizada pela Paix&atilde;o de Cristo, representativa de um grande sofrimento. O serm&atilde;o de Padre Ant&ocirc;nio Vieira se insere na l&oacute;gica da escraviza&ccedil;&atilde;o de negros africanos transmigrados compulsoriamente para o trabalho nos engenhos de a&ccedil;&uacute;car.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41383,8605,'D','<p>o papel dos senhores na administra&ccedil;&atilde;o dos engenhos.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>Os senhores de engenho n&atilde;o &ldquo;padeciam&rdquo; da mesma situa&ccedil;&atilde;o daquela simbolizada pela Paix&atilde;o de Cristo, representativa de um grande sofrimento. Eles ocupavam o posto de maior privil&eacute;gio dentro da hierarquia de poder pr&oacute;pria do engenho. Os seus lucros e sobreviv&ecirc;ncia dependiam da explora&ccedil;&atilde;o extrema de negros escravizados africanos transmigrados compulsoriamente para o trabalho nos engenhos de a&ccedil;&uacute;car. Padre Ant&ocirc;nio Vieira faz uma compara&ccedil;&atilde;o entre as condi&ccedil;&otilde;es de trabalho desses negros, impostas pelos senhores de engenho, e a Paix&atilde;o de Cristo.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41384,8605,'E','<p>o trabalho dos escravos na produ&ccedil;&atilde;o de a&ccedil;&uacute;car.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Imagens tais como um Cristo &ldquo;despido&rdquo;, &ldquo;maltratado&rdquo;, &ldquo;sem descanso&rdquo;, est&atilde;o ligadas &aacute; situa&ccedil;&atilde;o dos escravos que trabalhavam na produ&ccedil;&atilde;o de a&ccedil;&uacute;car. O serm&atilde;o de Padre Ant&ocirc;nio Vieira se insere na l&oacute;gica da escraviza&ccedil;&atilde;o de negros africanos transmigrados compulsoriamente para o trabalho nos engenhos de a&ccedil;&uacute;car. Padre Ant&ocirc;nio Vieira faz uma compara&ccedil;&atilde;o entre as condi&ccedil;&otilde;es de trabalho desses negros, impostas pelos senhores de engenho, e a Paix&atilde;o de Cristo.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41385,8606,'A','<p>elimina os conflitos no ambiente das f&aacute;bricas.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O discurso apresentado no texto tenta construir a concep&ccedil;&atilde;o de uma organiza&ccedil;&atilde;o sindical harmoniosa, que conjugasse diferentes interesses na rela&ccedil;&atilde;o vertical entre patr&otilde;es e empregados. Entretanto, n&atilde;o diz respeito especificamente a rela&ccedil;&otilde;es horizontais, no ambiente da f&aacute;brica. O aluno que marcou essa alternativa possivelmente n&atilde;o reconheceu que a solu&ccedil;&atilde;o apresentada no discurso do membro do minist&eacute;rio de Get&uacute;lio Vargas diz respeito a conflitos entre oper&aacute;rios e empres&aacute;rios.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41386,8606,'B','<p>limita os direitos associativos do segmento patronal.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O texto do item apresenta a no&ccedil;&atilde;o de um atendimento aos interesses dos oper&aacute;rios e patr&otilde;es, em uma harmoniosa organiza&ccedil;&atilde;o sindical, sem fazer men&ccedil;&atilde;o, entretanto, a regimes de associa&ccedil;&atilde;o espec&iacute;ficos de organiza&ccedil;&otilde;es patronais ou mesmo de oper&aacute;rios. &Eacute; mencionado que a organiza&ccedil;&atilde;o sindical n&atilde;o se limitou aos oper&aacute;rios, o que implica, portanto, que anseios dos &ldquo;patr&otilde;es&rdquo; tamb&eacute;m tenham sido atendidos. Assim, &eacute; poss&iacute;vel que o aluno que marcou essa quest&atilde;o n&atilde;o tenha dom&iacute;nio da composi&ccedil;&atilde;o do que seria o &ldquo;segmento patronal&rdquo;.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41387,8606,'C','<p>orienta a busca do consenso entre trabalhadores&nbsp;e patr&otilde;es.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>O discurso apresentado no item, retirado do Boletim do Minist&eacute;rio do Trabalho, Ind&uacute;stria e Com&eacute;rcio, &eacute; categ&oacute;rico em considerar que a organiza&ccedil;&atilde;o sindical estruturada pelo governo de Get&uacute;lio Vargas obteve sucesso em promover a harmonia entre &ldquo;capital e trabalho&rdquo;, ou seja, entre &ldquo;patr&atilde;o e oper&aacute;rio&rdquo;. O aluno alcan&ccedil;a o gabarito ao recuperar a imagem que a Era Vargas buscou perpetuar &ndash; a de um legado de solu&ccedil;&atilde;o dos conflitos sociais entre patr&otilde;es e empregados por meio da cria&ccedil;&atilde;o das leis trabalhistas &ndash; e ao considerar tamb&eacute;m o dom&iacute;nio sobre os sindicatos que o governo buscava, a fim de controlar suas a&ccedil;&otilde;es sem grandes preju&iacute;zos aos empres&aacute;rios. Essa postura do Estado &eacute; parte importante da hist&oacute;ria da luta sindical no pa&iacute;s.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41388,8606,'D','<p>pro&iacute;be o registro de estrangeiros nas entidades&nbsp;profissionais do pa&iacute;s.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A alternativa indica um resultado da organiza&ccedil;&atilde;o sindical incoerente com a realidade do governo Get&uacute;lio Vargas, que se anunciava pai dos trabalhadores do Brasil, e tamb&eacute;m com o discurso apresentado no texto, que n&atilde;o aponta para o caso de trabalhadores estrangeiros especificamente. O aluno que marcou essa alternativa possivelmente desconsiderou que grande parte do operariado nos primeiros anos da Rep&uacute;blica era composta por imigrantes de diferentes nacionalidades, e que foram important&iacute;ssimos na hist&oacute;ria sindical nacional.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41389,8606,'E','<p>desobriga o Estado quanto aos direitos e deveres da&nbsp;classe trabalhadora.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O discurso apresentado no item ilustra o papel que o governo brasileiro assumiu no Estado Novo, de estruturar a organiza&ccedil;&atilde;o sindical, o que invalida a alternativa. Ao contr&aacute;rio da desobriga&ccedil;&atilde;o sugerida nesse distrator, o Estado tomou para si a responsabilidade de arranjar a rela&ccedil;&atilde;o harmoniosa entre patr&otilde;es e empregados. O aluno que marcou essa alternativa provavelmente n&atilde;o considerou a imagem hist&oacute;rica constru&iacute;da em torno de Get&uacute;lio Vargas como &ldquo;pai dos trabalhadores&rdquo; e algu&eacute;m, portanto, que se preocupava com direitos e deveres do operariado.</p>\r\n',0);
