INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53779,11035,'E','<p>uma atiradeira (estilingue).</p>\r\n','<p>CORRETA.<br />\r\nA transforma&ccedil;&atilde;o de energia descrita no texto-base &eacute; a de energia el&aacute;stica em cin&eacute;tica. Em um estilingue, observamos a mesma transforma&ccedil;&atilde;o, da energia el&aacute;stica para energia cin&eacute;tica do proj&eacute;til.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41565,8642,'A','<p>transferir o contaminante do solo para a &aacute;gua.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que seleciona essa alternativa n&atilde;o compreende que se o microrganismo transferir o contaminante dos pesticidas do solo para &aacute;gua n&atilde;o haver&aacute; biorremedia&ccedil;&atilde;o, somente uma mudan&ccedil;a no local da contamina&ccedil;&atilde;o (na &aacute;gua, em vez do solo).</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41566,8642,'B','<p>absorver o contaminante sem alter&aacute;-lo quimicamente.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que seleciona essa alternativa n&atilde;o compreende que no processo de biorremedia&ccedil;&atilde;o, o microrganismo tem a fun&ccedil;&atilde;o de metabolizar o contaminante para liberar subprodutos menos t&oacute;xicos ou at&oacute;xicos ao meio ambiente.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41567,8642,'C','<p>apresentar alta taxa de muta&ccedil;&atilde;o ao longo das gera&ccedil;&otilde;es.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que seleciona essa alternativa n&atilde;o compreende que a taxa de muta&ccedil;&atilde;o n&atilde;o &eacute; uma caracter&iacute;stica relevante para selecionar um microrganismo para ser usado na t&eacute;cnica de biorremedia&ccedil;&atilde;o.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41568,8642,'D','<p>estimular o sistema imunol&oacute;gico do homem contra o contaminante.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O aluno que seleciona essa alternativa n&atilde;o compreende que o microrganismo a ser selecionado para a t&eacute;cnica de biorremedia&ccedil;&atilde;o ser&aacute; usado na &aacute;rea de contamina&ccedil;&atilde;o, n&atilde;o tendo nenhuma influ&ecirc;ncia sobre o sistema imunol&oacute;gico humano.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41569,8642,'E','<p>metabolizar o contaminante, liberando subprodutos menos t&oacute;xicos ou at&oacute;xicos.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Para que um microrganismo possa ser usado no processo de biorremedia&ccedil;&atilde;o de pesticidas &eacute; necess&aacute;rio que ele seja capaz de metabolizar os contaminantes a fim de que sejam liberados apenas subprodutos menos t&oacute;xicos ou at&oacute;xicos ao meio ambiente.</p>\r\n',1);
