INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41311,8591,'B','<p>0,33 kg.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Considerando rea&ccedil;&atilde;o de combust&atilde;o completa do butano, tem-se:</p>\r\n\r\n<p>C<sub>4</sub>H<sub>10</sub> + 6,5O<sub>2</sub> $\\rightarrow$ 4CO<sub>2</sub> + 5H<sub>2</sub>O</p>\r\n\r\n<p>58 g de butano - 4 x&nbsp;44 g de g&aacute;s carb&ocirc;nico</p>\r\n\r\n<p>x g de butano - 1 kg de g&aacute;s carb&ocirc;nico</p>\r\n\r\n<p>x = 0,3295 kg de butano $\\approx$ 0,33 kg</p>\r\n',1);
