INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41175,8564,'A','<p>a dedica&ccedil;&atilde;o de Policarpo Quaresma ao conhecimento da natureza brasileira levou-o a estudar inutilidades, mas possibilitou-lhe uma vis&atilde;o mais ampla do pa&iacute;s.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O distrator peca em duas de suas afirma&ccedil;&otilde;es, sendo a primeira aquela que diz respeito &agrave; dedica&ccedil;&atilde;o de Policarpo, que era ao Brasil como na&ccedil;&atilde;o e n&atilde;o &agrave; sua natureza. A segunda afirmativa errada &eacute; a que diz que estes estudos levaram Policarpo a um entendimento mais amplo sobre a na&ccedil;&atilde;o, sendo que sua dedica&ccedil;&atilde;o ao patriotismo o levou a um estudo direcionado e frustrante. O aluno pode escolher esse distrator caso foque nos elementos naturais descritos no fragmento.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41176,8564,'B','<p>a curiosidade em rela&ccedil;&atilde;o aos her&oacute;is da p&aacute;tria levou-o ao ideal de prosperidade e democracia que o personagem encontra no contexto republicano.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O fragmento mostra a conclus&atilde;o inef&aacute;vel que saber o nome dos her&oacute;is n&atilde;o levou a personagem a lugar algum. O aluno s&oacute; marcar&aacute; esse distrator com uma leitura rasa, na qual ele n&atilde;o localize o fragmento que aponta a inutilidade do conhecimento do nome dos her&oacute;is.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41177,8564,'C','<p>a constru&ccedil;&atilde;o de uma p&aacute;tria a partir de elementos m&iacute;ticos, como a cordialidade do povo, a riqueza do solo e a pureza lingu&iacute;stica, conduz &agrave; frustra&ccedil;&atilde;o ideol&oacute;gica.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>Todos os conhecimentos pr&eacute;-moldados inerentes ao patriotismo mostraram-se falhos para a personagem, portanto, foram motivos de frustra&ccedil;&atilde;o, j&aacute; que desmentiram tudo aquilo que lhe era ideal.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41178,8564,'D','<p>a propens&atilde;o do brasileiro ao riso, ao esc&aacute;rnio, justifica a rea&ccedil;&atilde;o de decep&ccedil;&atilde;o e desist&ecirc;ncia de Policarpo Quaresma, que prefere resguardar-se em seu gabinete.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>H&aacute; nesse distrator uma distor&ccedil;&atilde;o dos elementos que apareceram no fragmento, por exemplo, a cultura do esc&aacute;rnio &eacute; apenas uma das decep&ccedil;&otilde;es da personagem. Al&eacute;m disso, Policarpo n&atilde;o se resguardou em seu escrit&oacute;rio, ele criou todos esses ideais em seu escrit&oacute;rio, durante seus estudos. O aluno poder&aacute; ser induzido ao erro pelo tom conclusivo desse distrator.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41179,8564,'E','<p>a certeza da fertilidade da terra e da produ&ccedil;&atilde;o agr&iacute;cola incondicional faz parte de um projeto ideol&oacute;gico salvacionista, tal como foi difundido na &eacute;poca do autor.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>A certeza da fertilidade da terra foi um conceito amplamente difundido desde os per&iacute;odos de coloniza&ccedil;&atilde;o, n&atilde;o sendo exclusivos da &eacute;poca da personagem. Al&eacute;m disso, n&atilde;o h&aacute; algo no texto que remeta a um &ldquo;projeto ideol&oacute;gico salvacionista&rdquo;. O aluno pode escolher esse distrator caso ignore todas as outras frustra&ccedil;&otilde;es de Policarpo.</p>\r\n',0);
