INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41926,8714,'A','<p>pintura de modelos em planos irregulares.</p>\r\n','<p>CORRETA</p>\r\n\r\n<p>A pintura de modelos em planos irregulares &eacute; caracter&iacute;stica do Cubismo. Uma an&aacute;lise cuidadosa revela cinco personagens na composi&ccedil;&atilde;o, todas nuas, com seus corpos cinzelados rudimentarmente e com seus rostos esquem&aacute;ticos. No primeiro plano est&aacute; um prato com frutas. Ao centro, duas mulheres nuas que olham para frente, como se estivessem mirando o observador do quadro. &Agrave; direita, no canto inferior, encontra-se uma mulher sentada de costas para o observador do quadro, cujas fei&ccedil;&otilde;es, nariz, olhos e boca est&atilde;o completamente desalinhados. Assemelha-se a uma m&aacute;scara. &Agrave; direita, o canto superior, mais uma mulher que parece usar m&aacute;scara. &Agrave; esquerda, encontra-se a quinta mulher, posicionada de perfil.</p>\r\n',1);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (41927,8714,'B','<p>mulher como tem&aacute;tica central da obra.</p>\r\n','<p>INCORRETA</p>\r\n\r\n<p>O fato de s&oacute; haver mulheres no quadro pode, evidentemente, ludibriar o estudante. Entretanto, essa n&atilde;o &eacute; uma caracter&iacute;stica do Cubismo de maneira geral, mas espec&iacute;fica dessa obra.</p>\r\n',0);
