INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53555,10991,'A','<p>\r\n	o estabelecimento de uma norma prescinde de uma pesquisa hist&oacute;rica.</p>\r\n','<p>INCORRETA<br />\r\nO respondente poder&aacute; marcar essa afirmativa se n&atilde;o interpretar o texto corretamente, pois a autora cita casos de pesquisa em textos escritos dos anos quarenta e cinquenta do s&eacute;culo XVI, o que configura uma pesquisa. O aluno pode tamb&eacute;m n&atilde;o compreender o que significa a palavra &ldquo;prescinde&rdquo; (&ldquo;n&atilde;o precisar de&rdquo;, &ldquo;dispensar&rdquo;)</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53556,10991,'B','<p>\r\n	os estudos cl&aacute;ssicos de sintaxe hist&oacute;rica enfatizam a varia&ccedil;&atilde;o e a mudan&ccedil;a na l&iacute;ngua.</p>\r\n','<p>INCORRETA<br />\r\nEssa alternativa n&atilde;o se relaciona ao que &eacute; pedido no enunciado nem ao que o texto enfoca. O aluno que a escolher est&aacute; usando somente seu conhecimento sobre o assunto para deduzir que a afirmativa &eacute; correta.</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53557,10991,'C','<p>\r\n	a avalia&ccedil;&atilde;o cr&iacute;tica e hierarquizante dos usos da l&iacute;ngua fundamenta a defini&ccedil;&atilde;o da norma.</p>\r\n','<p>INCORRETA<br />\r\nHierarquizar os usos da l&iacute;ngua &eacute; fazer ju&iacute;zo de valor sobre as variedades da l&iacute;ngua, por exemplo, categorizar o uso ou o n&atilde;o uso da norma padr&atilde;o da l&iacute;ngua como sinal de intelig&ecirc;ncia ou garantia de conte&uacute;do. A reflex&atilde;o feita pela autora sobre o uso de &ldquo;ter&rdquo; e &ldquo;haver&rdquo; demonstra, ao contr&aacute;rio, que categorizar usos seguindo o purismo da l&iacute;ngua n&atilde;o serve como par&acirc;metro para compreender o fen&ocirc;meno lingu&iacute;stico.&nbsp;</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53558,10991,'D','<p>\r\n	a ado&ccedil;&atilde;o de uma &uacute;nica norma revela uma atitude adequada para os estudos lingu&iacute;sticos.</p>\r\n','<p>INCORRETA<br />\r\nO aluno que n&atilde;o interpretar o texto corretamente ou que usar somente seu conhecimento de senso comum julgar&aacute; tal resposta como correta. Ao contr&aacute;rio do que se afirma nessa alternativa, o texto apresenta a reflex&atilde;o de que a ado&ccedil;&atilde;o de uma &uacute;nica norma revela conhecimento deficiente da l&iacute;ngua.&nbsp;</p>\r\n',0);
INSERT INTO `alternatives` (`id`,`question_id`,`label`,`content`,`justification`,`correct`) VALUES (53559,10991,'E','<p>\r\n	os comportamentos puristas s&atilde;o prejudiciais &agrave; compreens&atilde;o da constitui&ccedil;&atilde;o lingu&iacute;stica.</p>\r\n','<p>CORRETA<br />\r\nEssa afirmativa revela o ponto de vista da autora ao afirmar que os comportamentos puristas s&atilde;o prejudiciais &agrave; compreens&atilde;o da constitui&ccedil;&atilde;o lingu&iacute;stica, ou seja, segundo ela, defender a pureza da linguagem de maneira limitada s&oacute; revela um conhecimento deficiente da l&iacute;ngua.</p>\r\n',1);
